   440‚Üífunction deleteClipsAtSilencePointsInTrack(silences, trackType, trackIndex) {
   441‚Üí    try {
   442‚Üí        silences = JSON.parse(silences);
   443‚Üí    } catch (e) {
   444‚Üí        return JSON.stringify({ error: "Invalid JSON input: " + e.message });
   445‚Üí    }
   446‚Üí    var clips;
   447‚Üí    if (trackType == "video") {
   448‚Üí        clips = app.project.activeSequence.videoTracks[trackIndex].clips;
   449‚Üí    } else if (trackType == "audio") {
   450‚Üí        clips = app.project.activeSequence.audioTracks[trackIndex].clips;
   451‚Üí    }
   452‚Üí
   453‚Üí    var deleted = 0;
   454‚Üí    for (var i = clips.length - 1; i >= 0; i--) {
   455‚Üí        var clip = clips[i];
   456‚Üí        var start = clip.start.seconds;
   457‚Üí        var end = clip.end.seconds;
   458‚Üí        var center = (start + end) / 2;
   459‚Üí
   460‚Üí        for (var j = 0; j < silences.length; j++) {
   461‚Üí            var silence = silences[j];
   462‚Üí            var silenceCenter = (silence[0] + silence[1]) / 2;
   463‚Üí            if (silenceCenter > start && silenceCenter < end) {
   464‚Üí                clip.remove(false, false);
   465‚Üí                deleted++;
   466‚Üí                break;
   467‚Üí            }
   468‚Üí        }
   469‚Üí    }
   470‚Üí    return JSON.stringify({ deleted: deleted });
   471‚Üí}
