   473‚Üífunction makeRemainingClipsContiguousInTrack(silences, deltas, trackType, trackIndex) {
   474‚Üí    try {
   475‚Üí        silences = JSON.parse(silences);
   476‚Üí    } catch (e) {
   477‚Üí        return JSON.stringify({ error: "Invalid JSON input for silences: " + e.message });
   478‚Üí    }
   479‚Üí    try {
   480‚Üí        deltas = JSON.parse(deltas);
   481‚Üí    } catch (e) {
   482‚Üí        return JSON.stringify({ error: "Invalid JSON input for deltas: " + e.message });
   483‚Üí    }
   484‚Üí
   485‚Üí    var clips;
   486‚Üí    if (trackType == "video") {
   487‚Üí        clips = app.project.activeSequence.videoTracks[trackIndex].clips;
   488‚Üí    } else if (trackType == "audio") {
   489‚Üí        clips = app.project.activeSequence.audioTracks[trackIndex].clips;
   490‚Üí    }
   491‚Üí
   492‚Üí    for (var i = 0; i < clips.length; i++) {
   493‚Üí        var clip = clips[i];
   494‚Üí        var end = clip.end.seconds;
   495‚Üí        var num_silences = -1;
   496‚Üí
   497‚Üí        for (var j = 0; j < silences.length; j++) {
   498‚Üí            if (end > silences[j][1]) {
   499‚Üí                num_silences++;
   500‚Üí            }
   501‚Üí        }
   502‚Üí
   503‚Üí        if (num_silences > -1 && num_silences < deltas.length) {
   504‚Üí            var delta = deltas[num_silences];
   505‚Üí            var t_start = clip.start;
   506‚Üí            t_start.seconds = t_start.seconds - delta;
   507‚Üí            clip.start = t_start;
   508‚Üí            var t_end = clip.end;
   509‚Üí            t_end.seconds = t_end.seconds - delta;
   510‚Üí            clip.end = t_end;
   511‚Üí        }
   512‚Üí    }
   513‚Üí    return JSON.stringify({ success: true });
   514‚Üí}
