   925‚Üífunction deleteAllMarkers() {
   926‚Üí    try {
   927‚Üí        var seq = app.project.activeSequence;
   928‚Üí        if (!seq) return JSON.stringify({ error: "No active sequence" });
   929‚Üí
   930‚Üí        var markers = seq.markers;
   931‚Üí        var count = markers.numMarkers;
   932‚Üí
   933‚Üí        // Delete from end to beginning to avoid index shifting
   934‚Üí        for (var i = count - 1; i >= 0; i--) {
   935‚Üí            markers[i].remove();
   936‚Üí        }
