  1003‚Üí
  1004‚Üífunction buildSequenceFromCutList(cutListJson) {
  1005‚Üí    try {
  1006‚Üí        var cutList;
  1007‚Üí        try {
  1008‚Üí            cutList = JSON.parse(cutListJson);
  1009‚Üí        } catch (e) {
  1010‚Üí            return JSON.stringify({ error: "Invalid JSON input: " + e.message });
  1011‚Üí        }
  1012‚Üí        var seq = app.project.activeSequence;
  1013‚Üí        if (!seq) return JSON.stringify({ error: "No active sequence" });
  1014‚Üí
  1015‚Üí        // Clone the sequence
  1016‚Üí        var newSequenceName = seq.name + "_SPLICE";
  1017‚Üí        seq.clone();
  1018‚Üí
  1019‚Üí        // Find the cloned sequence
  1020‚Üí        var newSeq = null;
  1021‚Üí        for (var i = app.project.sequences.numSequences - 1; i >= 0; i--) {
  1022‚Üí            var s = app.project.sequences[i];
  1023‚Üí            if (s.name.indexOf(seq.name) === 0 && s.sequenceID !== seq.sequenceID) {
  1024‚Üí                newSeq = s;
  1025‚Üí                break;
  1026‚Üí            }
  1027‚Üí        }
  1028‚Üí
  1029‚Üí        if (!newSeq) {
  1030‚Üí            return JSON.stringify({ error: "Failed to clone sequence" });
  1031‚Üí        }
  1032‚Üí
  1033‚Üí        newSeq.name = newSequenceName;
  1034‚Üí        app.project.activeSequence = newSeq;
  1035‚Üí
  1036‚Üí        // Clear all clips from the new sequence
  1037‚Üí        clearSequence();
  1038‚Üí
  1039‚Üí        // Insert segments
  1040‚Üí        var currentPosition = 0;
  1041‚Üí        var stats = {
  1042‚Üí            segmentsInserted: 0,
  1043‚Üí            totalDuration: 0
  1044‚Üí        };
  1045‚Üí
  1046‚Üí        for (var i = 0; i < cutList.segments.length; i++) {
  1047‚Üí            var segment = cutList.segments[i];
  1048‚Üí
  1049‚Üí            // Find the source project item
  1050‚Üí            var sourceItem = null;
  1051‚Üí            if (segment.sourcePath) {
  1052‚Üí                sourceItem = findItemByPath(app.project.rootItem, segment.sourcePath);
  1053‚Üí            }
  1054‚Üí            if (!sourceItem && segment.sourceName) {
  1055‚Üí                sourceItem = findItemByName(app.project.rootItem, segment.sourceName);
  1056‚Üí            }
  1057‚Üí
  1058‚Üí            if (!sourceItem) continue;
  1059‚Üí
  1060‚Üí            // Set color label
  1061‚Üí            if (segment.colorHint && SEGMENT_COLORS[segment.colorHint]) {
  1062‚Üí                sourceItem.setColorLabel(SEGMENT_COLORS[segment.colorHint]);
  1063‚Üí            } else if (segment.type && SEGMENT_COLORS[segment.type]) {
  1064‚Üí                sourceItem.setColorLabel(SEGMENT_COLORS[segment.type]);
  1065‚Üí            }
  1066‚Üí
  1067‚Üí            // Insert clip at current position
  1068‚Üí            var targetTime = new Time();
  1069‚Üí            targetTime.seconds = currentPosition;
  1070‚Üí
  1071‚Üí            newSeq.videoTracks[0].insertClip(sourceItem, targetTime);
  1072‚Üí
  1073‚Üí            // Set in/out points
  1074‚Üí            var clipDuration = segment.outPoint - segment.inPoint;
  1075‚Üí            var videoClips = newSeq.videoTracks[0].clips;
  1076‚Üí            var insertedClip = videoClips[videoClips.numItems - 1];
  1077‚Üí
  1078‚Üí            if (insertedClip) {
  1079‚Üí                var inTime = new Time();
  1080‚Üí                inTime.seconds = segment.inPoint;
  1081‚Üí                insertedClip.inPoint = inTime;
  1082‚Üí
  1083‚Üí                var outTime = new Time();
  1084‚Üí                outTime.seconds = segment.outPoint;
  1085‚Üí                insertedClip.outPoint = outTime;
  1086‚Üí
  1087‚Üí                // Set clip name if takeLabel provided
  1088‚Üí                if (segment.takeLabel) {
  1089‚Üí                    insertedClip.name = segment.takeLabel;
  1090‚Üí                }
  1091‚Üí            }
  1092‚Üí
  1093‚Üí            currentPosition += clipDuration;
  1094‚Üí            stats.segmentsInserted++;
  1095‚Üí            stats.totalDuration += clipDuration;
  1096‚Üí        }
  1097‚Üí
  1098‚Üí        return JSON.stringify({
  1099‚Üí            success: true,
  1100‚Üí            sequenceName: newSequenceName,
  1101‚Üí            stats: stats
  1102‚Üí        });
  1103‚Üí    } catch (e) {
  1104‚Üí        return JSON.stringify({ error: e.message });
  1105‚Üí    }
  1106‚Üí}
