  1111‚Üí
  1112‚Üí    // Remove all video clips
  1113‚Üí    for (var t = 0; t < seq.videoTracks.numTracks; t++) {
  1114‚Üí        var clips = seq.videoTracks[t].clips;
  1115‚Üí        for (var i = clips.numItems - 1; i >= 0; i--) {
