  1119‚Üí
  1120‚Üí    // Remove all audio clips
  1121‚Üí    for (var t = 0; t < seq.audioTracks.numTracks; t++) {
  1122‚Üí        var clips = seq.audioTracks[t].clips;
  1123‚Üí        for (var i = clips.numItems - 1; i >= 0; i--) {
  1124‚Üí            clips[i].remove(false, false);
  1125‚Üí        }
  1126‚Üí    }
  1127‚Üí
  1128‚Üí    return true;
  1129‚Üí}
