  1316‚Üífunction moveClipByNTracks(currentTrackId, clipStartTime, nTracksToMove, trackType) {
  1317‚Üí    if (!isQEAvailable() && !enableQE()) {
  1318‚Üí        return JSON.stringify({ error: "QE not available for moving clips between tracks" });
  1319‚Üí    }
  1320‚Üí
  1321‚Üí    try {
  1322‚Üí        var accuracy = 3;
  1323‚Üí        var search_start_time = clipStartTime.toFixed(accuracy);
  1324‚Üí
  1325‚Üí        if (trackType == "audio") {
  1326‚Üí            var numClipsInTrack = qe.project.getActiveSequence().getAudioTrackAt(currentTrackId).numItems;
  1327‚Üí            for (var j = 0; j < numClipsInTrack; j++) {
  1328‚Üí                if (qe.project.getActiveSequence().getAudioTrackAt(currentTrackId).getItemAt(j).start.secs.toFixed(accuracy) == search_start_time) {
  1329‚Üí                    qe.project.getActiveSequence().getAudioTrackAt(currentTrackId).getItemAt(j).moveToTrack(0, nTracksToMove, "0");
  1330‚Üí                    return JSON.stringify({ success: true });
  1331‚Üí                }
  1332‚Üí            }
  1333‚Üí        } else if (trackType == "video") {
  1334‚Üí            var numClipsInTrack = qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).numItems;
  1335‚Üí            for (var j = 0; j < numClipsInTrack; j++) {
  1336‚Üí                if (qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).getItemAt(j).start.secs.toFixed(accuracy) == search_start_time) {
  1337‚Üí                    qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).getItemAt(j).moveToTrack(nTracksToMove, 0, "0");
  1338‚Üí                    return JSON.stringify({ success: true });
  1339‚Üí                }
  1340‚Üí            }
  1341‚Üí        }
  1342‚Üí        return JSON.stringify({ error: "Clip not found at specified time" });
  1343‚Üí    } catch (e) {
  1344‚Üí        return JSON.stringify({ error: "Move clip failed: " + e.message });
  1345‚Üí    }
  1346‚Üí}
