  1348‚Üífunction linkIdenticalClipsInTracks(videoTrackIndex, audioTrackIndex) {
  1349‚Üí    var selected_clips = app.project.activeSequence.getSelection();
  1350‚Üí    for (var i = 0; i < selected_clips.length; i++) {
  1351‚Üí        selected_clips[i].setSelected(false, false);
  1352‚Üí    }
  1353‚Üí
  1354‚Üí    var video_clips = app.project.activeSequence.videoTracks[videoTrackIndex].clips;
  1355‚Üí    var audio_clips = app.project.activeSequence.audioTracks[audioTrackIndex].clips;
  1356‚Üí
  1357‚Üí    for (var i = 0; i < video_clips.numItems; i++) {
  1358‚Üí        var video_clip = video_clips[i];
  1359‚Üí        var video_start = video_clip.start.seconds;
  1360‚Üí
  1361‚Üí        for (var j = 0; j < audio_clips.numItems; j++) {
  1362‚Üí            var audio_clip = audio_clips[j];
  1363‚Üí            var audio_start = audio_clip.start.seconds;
  1364‚Üí
  1365‚Üí            if (Math.abs(video_start - audio_start) < time_tolerance) {
  1366‚Üí                video_clip.setSelected(true, false);
  1367‚Üí                audio_clip.setSelected(true, false);
  1368‚Üí                app.project.activeSequence.linkSelection();
  1369‚Üí                video_clip.setSelected(false, false);
  1370‚Üí                audio_clip.setSelected(false, false);
  1371‚Üí                break;
  1372‚Üí            }
  1373‚Üí        }
  1374‚Üí    }
  1375‚Üí    return true;
  1376‚Üí}
