     1‚Üí/**
     2‚Üí * SPLICE CEP Browser Simulation
     3‚Üí * Mocks Adobe CEP environment and Backend APIs for full feature testing in browser.
     4‚Üí * IMPORTANT: This script auto-disables in real CEP environments (Premiere Pro)
     5‚Üí */
     6‚Üí
     7‚Üí(function () {
     8‚Üí    // Skip simulation if running in real Adobe CEP environment
     9‚Üí    if (window.__adobe_cep__) {
    10‚Üí        console.log('%c [SPLICE] Real CEP environment detected - simulation disabled ', 'background: #4CAF50; color: white;');
    11‚Üí        return; // Exit immediately, don't mock anything
    12‚Üí    }
    13‚Üí
    14‚Üí    console.log('%c [SPLICE SIMULATION] Initializing... ', 'background: #00d1b2; color: black; font-weight: bold;');
    15‚Üí
    16‚Üí    // ============================================================================
    17‚Üí    // API CALL TRACKING (for comprehensive testing)
    18‚Üí    // ============================================================================
    19‚Üí    window._apiCallTracker = {
    20‚Üí        calls: [],
    21‚Üí        clear: function () { this.calls = []; },
    22‚Üí        log: function (endpoint, method, body) {
    23‚Üí            this.calls.push({
    24‚Üí                endpoint,
    25‚Üí                method,
    26‚Üí                body,
    27‚Üí                timestamp: Date.now()
    28‚Üí            });
    29‚Üí        },
    30‚Üí        getCallsTo: function (endpoint) {
    31‚Üí            return this.calls.filter(c => c.endpoint.includes(endpoint));
    32‚Üí        },
    33‚Üí        getLastCall: function () {
    34‚Üí            return this.calls[this.calls.length - 1];
    35‚Üí        },
    36‚Üí        wasCalledWith: function (endpoint, bodyContains) {
    37‚Üí            return this.calls.some(c =>
    38‚Üí                c.endpoint.includes(endpoint) &&
    39‚Üí                (!bodyContains || (c.body && JSON.stringify(c.body).includes(bodyContains)))
    40‚Üí            );
    41‚Üí        }
    42‚Üí    };
    43‚Üí
    44‚Üí    // ============================================================================
    45‚Üí    // MODULE INITIALIZATION TRACKING
    46‚Üí    // ============================================================================
    47‚Üí    window._moduleTracker = {
    48‚Üí        initialized: {},
    49‚Üí        mark: function (moduleName) {
    50‚Üí            this.initialized[moduleName] = Date.now();
    51‚Üí        },
    52‚Üí        isInitialized: function (moduleName) {
    53‚Üí            return !!this.initialized[moduleName];
    54‚Üí        },
    55‚Üí        getAll: function () {
    56‚Üí            return Object.keys(this.initialized);
    57‚Üí        },
    58‚Üí        reset: function () {
    59‚Üí            this.initialized = {};
    60‚Üí        }
    61‚Üí    };
    62‚Üí
    63‚Üí    // ============================================================================
    64‚Üí    // UI STATE TRACKING (for verifying UI updates)
    65‚Üí    // ============================================================================
    66‚Üí    window._uiStateTracker = {
    67‚Üí        events: [],
    68‚Üí        log: function (component, action, data) {
    69‚Üí            this.events.push({ component, action, data, timestamp: Date.now() });
    70‚Üí        },
    71‚Üí        getEventsFor: function (component) {
    72‚Üí            return this.events.filter(e => e.component === component);
    73‚Üí        },
    74‚Üí        clear: function () { this.events = []; }
    75‚Üí    };
    76‚Üí
    77‚Üí    // ============================================================================
    78‚Üí    // 0. MOCK window.__adobe_cep__ (MUST BE FIRST)
    79‚Üí    // ============================================================================
    80‚Üí    // This enables CSInterface.js to work in browser mode
    81‚Üí    if (!window.__adobe_cep__) {
    82‚Üí        window.__adobe_cep__ = {
    83‚Üí            evalScript: function (script, callback) {
    84‚Üí                console.log(`%c[JSX Mock] ${script.substring(0, 60)}${script.length > 60 ? '...' : ''}`, 'color: #aaa');
    85‚Üí
    86‚Üí                const cmd = script.split('(')[0];
