    // This pre-fills 5 silences, enables all tiers, and prepares the environment.

    window.enableDannyMode = function () {
        console.log('%c [DANNY MODE] Activating Full Feature Test Suite... ', 'background: #FFD700; color: black; font-weight: bold;');

        // 1. Set Credentials
        try {
            localStorage.setItem('splice_license_key', 'SPLICE-Y2Q9-6G9G-MFQE');
            localStorage.setItem('splice_user_email', 'danny@splice-beta.test');
            // Mock backend license check to return Team Tier
            window._mockScenario = 'danny_team_tier';
        } catch (e) {
            console.error('[DANNY MODE] Failed to set localStorage credentials:', e);
        }

        // 2. Mock Complex Content (Multiple Silences)
