                        { start: 45.0, end: 50.1 },
                        { start: 58.5, end: 59.9 }
                    ]
                }), { status: 200 });
            }
            if (url.includes('/license/activate')) {
                return new Response(JSON.stringify({
                    success: true,
                    customerId: 'cus_beta_danny_isakov',
                    plan: 'team',
                    creditsRemaining: 999999, // Unlimited
                    features: { music: true, analysis: true, export: true }
                }), { status: 200 });
            }
            // Pass through to original mock logic
            return originalFetch(url, options);
        };

        console.log('[DANNY MODE] Ready. Reload panel or re-run checks.');
    };

    console.log('[SPLICE SIMULATION] Ready. "Go" button and all modules should function.');
    console.log('[SPLICE SIMULATION] Run `window.enableDannyMode()` to setup Danny\'s test environment.');
})();

