     1‚Üí// Mocking the Adobe ExtendScript environment
     2‚Üíconst $ = {
     3‚Üí    writeln: (msg) => console.log(msg)
     4‚Üí};
     5‚Üí
     6‚Üí// Global mocks
     7‚Üílet app = {
     8‚Üí    project: {
     9‚Üí        sequences: [],
    10‚Üí        activeSequence: null
    11‚Üí    }
    12‚Üí};
    13‚Üí
    14‚Üílet qe = {
    15‚Üí    project: {
    16‚Üí        getActiveSequence: () => null
    17‚Üí    }
    18‚Üí};
    19‚Üí
    20‚Üí// Mock helper function
    21‚Üífunction enableQE() {
    22‚Üí    return true;
    23‚Üí}
    24‚Üí
    25‚Üí// ---------------------------------------------------------
    26‚Üí// The Function Under Test (Copied from splice-cep/jsx/hostScript.jsx)
    27‚Üí// ---------------------------------------------------------
    28‚Üífunction checkSequenceOpen() {
    29‚Üí    try {
    30‚Üí        // Quick validation checks without logging every step
    31‚Üí        if (!app || !app.project || !app.project.sequences) {
    32‚Üí            $.writeln('[SPLICE] checkSequenceOpen: ERROR - Premiere Pro app/project not ready');
    33‚Üí            return false;
    34‚Üí        }
    35‚Üí
    36‚Üí        // Primary check: Standard DOM activeSequence
    37‚Üí        if (app.project.activeSequence) {
    38‚Üí            var seq = app.project.activeSequence;
    39‚Üí            if (seq.name && seq.sequenceID) {
    40‚Üí                $.writeln('[SPLICE] checkSequenceOpen: ‚úì Active sequence found: ' + seq.name);
    41‚Üí                return true;
    42‚Üí            }
    43‚Üí        }
    44‚Üí
    45‚Üí        // Fallback: Try QE DOM if standard DOM failed
    46‚Üí        if (enableQE()) {
    47‚Üí            try {
    48‚Üí                var qeSeq = qe.project.getActiveSequence();
    49‚Üí                if (qeSeq && qeSeq.name) {
    50‚Üí                    $.writeln('[SPLICE] checkSequenceOpen: ‚úì Active sequence found via QE: ' + qeSeq.name);
    51‚Üí                    return true;
    52‚Üí                }
    53‚Üí            } catch (qeErr) {
    54‚Üí                // Silent fallback failure - not critical
    55‚Üí            }
    56‚Üí        }
    57‚Üí
    58‚Üí        $.writeln('[SPLICE] checkSequenceOpen: ‚úó No active sequence found');
    59‚Üí        return false;
    60‚Üí    } catch (e) {
    61‚Üí        $.writeln('[SPLICE] checkSequenceOpen: ERROR - ' + e.message);
    62‚Üí        return false;
    63‚Üí    }
    64‚Üí}
    65‚Üí
    66‚Üí// ---------------------------------------------------------
    67‚Üí// Test Runner
    68‚Üí// ---------------------------------------------------------
    69‚Üí
    70‚Üíconsole.log("=== Running Simulation Tests for checkSequenceOpen ===\n");
    71‚Üí
    72‚Üí// Test 1: Standard API Success
    73‚Üíconsole.log("Test 1: Standard API returns active sequence");
    74‚Üíapp.project.activeSequence = { name: "Main Sequence", sequenceID: "12345" };
    75‚Üíconst result1 = checkSequenceOpen();
    76‚Üíif (result1 === true) console.log("Result: PASS");
    77‚Üíelse console.log("Result: FAIL");
    78‚Üíconsole.log("---------------------------------------------------------");
    79‚Üí
    80‚Üí// Test 2: Standard API returns null, but QE returns sequence
    81‚Üíconsole.log("Test 2: Standard API fails, QE fallback succeeds");
    82‚Üíapp.project.activeSequence = null; // Standard API fails
    83‚Üíqe.project.getActiveSequence = () => ({ name: "QE Sequence" }); // QE succeeds
    84‚Üíconst result2 = checkSequenceOpen();
    85‚Üíif (result2 === true) console.log("Result: PASS");
    86‚Üíelse console.log("Result: FAIL");
    87‚Üíconsole.log("---------------------------------------------------------");
    88‚Üí
    89‚Üí// Test 3: Both APIs fail
    90‚Üíconsole.log("Test 3: No active sequence anywhere");
    91‚Üíapp.project.activeSequence = null;
    92‚Üíqe.project.getActiveSequence = () => null;
    93‚Üíconst result3 = checkSequenceOpen();
    94‚Üíif (result3 === false) console.log("Result: PASS");
    95‚Üíelse console.log("Result: FAIL");
    96‚Üíconsole.log("---------------------------------------------------------");
