// Mocking the Adobe ExtendScript environment
const $ = {
    writeln: (msg) => console.log(msg)
};

// Global mocks
let app = {
    project: {
        sequences: [],
        activeSequence: null
    }
};

let qe = {
    project: {
        getActiveSequence: () => null
    }
};

// Mock helper function
function enableQE() {
    return true;
}

// ---------------------------------------------------------
// The Function Under Test (Copied from splice-cep/jsx/hostScript.jsx)
// ---------------------------------------------------------
function checkSequenceOpen() {
    try {
        // Quick validation checks without logging every step
        if (!app || !app.project || !app.project.sequences) {
            $.writeln('[SPLICE] checkSequenceOpen: ERROR - Premiere Pro app/project not ready');
            return false;
        }

        // Primary check: Standard DOM activeSequence
        if (app.project.activeSequence) {
            var seq = app.project.activeSequence;
            if (seq.name && seq.sequenceID) {
                $.writeln('[SPLICE] checkSequenceOpen: ‚úì Active sequence found: ' + seq.name);
                return true;
            }
        }

        // Fallback: Try QE DOM if standard DOM failed
        if (enableQE()) {
            try {
                var qeSeq = qe.project.getActiveSequence();
                if (qeSeq && qeSeq.name) {
                    $.writeln('[SPLICE] checkSequenceOpen: ‚úì Active sequence found via QE: ' + qeSeq.name);
                    return true;
                }
            } catch (qeErr) {
                // Silent fallback failure - not critical
            }
        }

        $.writeln('[SPLICE] checkSequenceOpen: ‚úó No active sequence found');
        return false;
    } catch (e) {
        $.writeln('[SPLICE] checkSequenceOpen: ERROR - ' + e.message);
        return false;
    }
}

// ---------------------------------------------------------
// Test Runner
// ---------------------------------------------------------

console.log("=== Running Simulation Tests for checkSequenceOpen ===\n");

// Test 1: Standard API Success
console.log("Test 1: Standard API returns active sequence");
app.project.activeSequence = { name: "Main Sequence", sequenceID: "12345" };
const result1 = checkSequenceOpen();
if (result1 === true) console.log("Result: PASS");
else console.log("Result: FAIL");
console.log("---------------------------------------------------------");

// Test 2: Standard API returns null, but QE returns sequence
console.log("Test 2: Standard API fails, QE fallback succeeds");
app.project.activeSequence = null; // Standard API fails
qe.project.getActiveSequence = () => ({ name: "QE Sequence" }); // QE succeeds
const result2 = checkSequenceOpen();
if (result2 === true) console.log("Result: PASS");
else console.log("Result: FAIL");
console.log("---------------------------------------------------------");

// Test 3: Both APIs fail
console.log("Test 3: No active sequence anywhere");
app.project.activeSequence = null;
qe.project.getActiveSequence = () => null;
const result3 = checkSequenceOpen();
if (result3 === false) console.log("Result: PASS");
else console.log("Result: FAIL");
console.log("---------------------------------------------------------");

// Test 4: App Not Ready
console.log("Test 4: App structure missing (Not Ready)");
const originalApp = app;
app = null; // Simulate app not ready
const result4 = checkSequenceOpen();
if (result4 === false) console.log("Result: PASS");
else console.log("Result: FAIL");
app = originalApp; // Restore
console.log("---------------------------------------------------------");

