function initModals() {
    // Settings modal
    if (ui.settingsBtn && ui.settingsModal) {
        ui.settingsBtn.addEventListener('click', () => {
            ui.settingsModal.classList.remove('hidden');
            // Sync remember options checkbox with current settings
