window.spliceDebug = {
    getState: () => ({
        silences: previewSilences,
        takes: previewTakes,
        chapters: currentChapters,
        zooms: currentZooms,
        selected: Array.from(selectedSilenceIndices)
    }),
    jsx,
    runDetection
};

