     1→/**
     2→ * SPLICE CEP Host Script
     3→ * ExtendScript for Premiere Pro
     4→ *
     5→ * Provides all Premiere Pro API functions called via jsx.evalScript()
     6→ * Based on proven patterns from FireCut and SPLICE UXP builder
     7→ */
     8→
     9→// Include shared utilities (constants, time conversion)
    10→#include "jsxUtils.jsx"
    11→
    12→// ============================================================================
    13→// RUNTIME STATE (not in jsxUtils because they are mutable)
    14→// ============================================================================
    15→var temp_bin = null;
    16→var sequence_default_timecode = null;
    17→
    18→// ============================================================================
    19→// INITIALIZATION
    20→// ============================================================================
    21→
    22→/**
    23→ * Check if QE (Quality Engine) DOM is available
    24→ * QE provides extended functionality not available in standard DOM
    25→ */
    26→function isQEAvailable() {
    27→    try {
    28→        return typeof qe !== 'undefined' && qe !== null && qe.project;
    29→    } catch (e) {
    30→        return false;
    31→    }
    32→}
    33→
    34→/**
    35→ * Enable QE DOM if not already enabled
    36→ * @returns {boolean} true if QE is now available
    37→ */
    38→function enableQE() {
    39→    try {
    40→        if (isQEAvailable()) return true;
    41→        app.enableQE();
    42→        return isQEAvailable();
    43→    } catch (e) {
    44→        $.writeln('[SPLICE] enableQE failed: ' + e.message);
    45→        return false;
    46→    }
    47→}
    48→
    49→/**
    50→ * Safe wrapper for QE operations
    51→ * @param {Function} operation - The QE operation to execute
    52→ * @param {string} operationName - Name for error reporting
    53→ * @returns {Object} Result or error object
    54→ */
    55→function safeQEOperation(operation, operationName) {
    56→    try {
    57→        if (!isQEAvailable()) {
    58→            if (!enableQE()) {
    59→                return { error: "QE not available. Please ensure Premiere Pro supports QE DOM." };
    60→            }
    61→        }
    62→        return operation();
    63→    } catch (e) {
    64→        return { error: operationName + " failed: " + e.message };
    65→    }
    66→}
    67→
    68→function initialise() {
    69→    storeDefaultTimecode();
    70→    // Try to enable QE on startup
    71→    var qeStatus = enableQE();
    72→    return JSON.stringify({ success: true, version: SPLICE_VERSION, qeAvailable: qeStatus });
    73→}
    74→
    75→function storeDefaultTimecode() {
    76→    try {
    77→        if (!app || !app.project || !app.project.activeSequence) {
    78→            $.writeln('[SPLICE] storeDefaultTimecode: No active sequence available');
    79→            return false;
    80→        }
    81→        sequence_default_timecode = app.project.activeSequence.getSettings().videoDisplayFormat;
    82→        $.writeln('[SPLICE] storeDefaultTimecode: Stored format: ' + sequence_default_timecode);
    83→        return true;
    84→    } catch (e) {
    85→        $.writeln('[SPLICE] storeDefaultTimecode failed: ' + e.message);
    86→        return false;
    87→    }
    88→}
    89→
    90→function checkSequenceOpen() {
    91→    try {
    92→        // Quick validation checks without logging every step
    93→        if (!app || !app.project || !app.project.sequences) {
    94→            $.writeln('[SPLICE] checkSequenceOpen: ERROR - Premiere Pro app/project not ready');
    95→            return false;
    96→        }
    97→
    98→        // Primary check: Standard DOM activeSequence
    99→        if (app.project.activeSequence) {
   100→            var seq = app.project.activeSequence;
   101→            if (seq.name && seq.sequenceID) {
   102→                $.writeln('[SPLICE] checkSequenceOpen: ✓ Active sequence found: ' + seq.name);
   103→                return true;
   104→            }
   105→        }
   106→
   107→        // Fallback: Try QE DOM if standard DOM failed
   108→        if (enableQE()) {
   109→            try {
   110→                var qeSeq = qe.project.getActiveSequence();
   111→                if (qeSeq && qeSeq.name) {
   112→                    $.writeln('[SPLICE] checkSequenceOpen: ✓ Active sequence found via QE: ' + qeSeq.name);
   113→                    return true;
   114→                }
   115→            } catch (qeErr) {
   116→                // Silent fallback failure - not critical
   117→            }
   118→        }
   119→
   120→        $.writeln('[SPLICE] checkSequenceOpen: ✗ No active sequence found');
   121→        return false;
   122→    } catch (e) {
   123→        $.writeln('[SPLICE] checkSequenceOpen: ERROR - ' + e.message);
   124→        return false;
   125→    }
   126→}
   127→
   128→function checkPremiereReady() {
   129→    try {
   130→        // Check if core Premiere Pro APIs are available and initialized
   131→        if (!app || typeof app === 'undefined') {
   132→            $.writeln('[SPLICE] checkPremiereReady: app object not available');
   133→            return false;
   134→        }
   135→
   136→        if (!app.project || typeof app.project === 'undefined') {
   137→            $.writeln('[SPLICE] checkPremiereReady: app.project not available');
   138→            return false;
   139→        }
   140→
   141→        if (!app.project.sequences || typeof app.project.sequences === 'undefined') {
   142→            $.writeln('[SPLICE] checkPremiereReady: app.project.sequences not available');
   143→            return false;
   144→        }
   145→
   146→        $.writeln('[SPLICE] checkPremiereReady: ✓ Premiere Pro APIs ready');
   147→        return true;
   148→    } catch (e) {
   149→        $.writeln('[SPLICE] checkPremiereReady: ERROR - ' + e.message);
   150→        return false;
   151→    }
   152→}
   153→
   154→function getVersion() {
   155→    return SPLICE_VERSION;
   156→}
   157→
   158→// TIME CONVERSION UTILITIES and TIMECODE FORMAT are now in jsxUtils.jsx (included above)
   159→
   160→// ============================================================================
   161→// SEQUENCE & PROJECT OPERATIONS
   162→// ============================================================================
   163→
   164→function getActiveSequence() {
   165→    var seq = app.project.activeSequence;
   166→    if (!seq) return JSON.stringify({ error: "No active sequence" });
   167→
   168→    return JSON.stringify({
   169→        name: seq.name,
   170→        id: seq.sequenceID,
   171→        duration: seq.end,
   172→        videoTrackCount: seq.videoTracks.numTracks,
   173→        audioTrackCount: seq.audioTracks.numTracks,
   174→        frameRate: seq.getSettings().videoFrameRate.seconds,
   175→        width: seq.frameSizeHorizontal,
   176→        height: seq.frameSizeVertical
   177→    });
   178→}
   179→
   180→function getSequenceSettings() {
   181→    var seq = app.project.activeSequence;
   182→    if (!seq) return JSON.stringify({ error: "No active sequence" });
   183→
   184→    var settings = seq.getSettings();
   185→    return JSON.stringify({
   186→        frameRate: settings.videoFrameRate.seconds,
   187→        width: settings.videoFrameWidth,
   188→        height: settings.videoFrameHeight,
   189→        displayFormat: settings.videoDisplayFormat
   190→    });
   191→}
   192→
   193→function cloneSequence(newName) {
   194→    var seq = app.project.activeSequence;
   195→    if (!seq) return JSON.stringify({ error: "No active sequence" });
   196→
   197→    try {
   198→        // Clone the sequence
   199→        seq.clone();
   200→
   201→        // Find the cloned sequence (last one with matching name pattern)
   202→        var clonedSeq = null;
   203→        for (var i = app.project.sequences.numSequences - 1; i >= 0; i--) {
   204→            var s = app.project.sequences[i];
   205→            if (s.name.indexOf(seq.name) === 0 && s.sequenceID !== seq.sequenceID) {
   206→                clonedSeq = s;
   207→                break;
   208→            }
   209→        }
   210→
   211→        if (clonedSeq && newName) {
   212→            clonedSeq.name = newName;
   213→        }
   214→
   215→        return JSON.stringify({
   216→            success: true,
   217→            sequenceId: clonedSeq ? clonedSeq.sequenceID : null,
   218→            name: clonedSeq ? clonedSeq.name : newName
   219→        });
   220→    } catch (e) {
   221→        return JSON.stringify({ error: e.message });
   222→    }
   223→}
   224→
   225→function createNewSequence(name) {
   226→    try {
   227→        app.project.createNewSequence(name);
   228→        var seq = app.project.activeSequence;
   229→        return JSON.stringify({
   230→            success: true,
   231→            sequenceId: seq.sequenceID,
   232→            name: seq.name
   233→        });
   234→    } catch (e) {
   235→        return JSON.stringify({ error: e.message });
   236→    }
   237→}
   238→
   239→function setActiveSequence(sequenceId) {
   240→    for (var i = 0; i < app.project.sequences.numSequences; i++) {
   241→        if (app.project.sequences[i].sequenceID === sequenceId) {
   242→            app.project.activeSequence = app.project.sequences[i];
   243→            return JSON.stringify({ success: true });
   244→        }
   245→    }
   246→    return JSON.stringify({ error: "Sequence not found" });
   247→}
   248→
   249→/**
   250→ * Seek playhead to a specific time in seconds
   251→ */
   252→function seekToTime(seconds) {
   253→    var seq = app.project.activeSequence;
   254→    if (!seq) return JSON.stringify({ error: "No active sequence" });
   255→
   256→    try {
   257→        // Convert seconds to ticks
   258→        var ticks = seconds * TICKS_PER_SECOND;
   259→
   260→        // Create a Time object and set position
   261→        var playerPos = seq.getPlayerPosition();
   262→        playerPos.seconds = seconds;
   263→        seq.setPlayerPosition(playerPos.ticks.toString());
   264→
   265→        return JSON.stringify({ success: true, position: seconds });
   266→    } catch (e) {
   267→        return JSON.stringify({ error: e.message });
   268→    }
   269→}
   270→
   271→/**
   272→ * Get current playhead position in seconds
   273→ */
   274→function getPlayerPosition() {
   275→    var seq = app.project.activeSequence;
   276→    if (!seq) return JSON.stringify({ error: "No active sequence" });
   277→
   278→    try {
   279→        var pos = seq.getPlayerPosition();
   280→        return JSON.stringify({
   281→            ticks: pos.ticks,
   282→            seconds: pos.seconds
   283→        });
   284→    } catch (e) {
   285→        return JSON.stringify({ error: e.message });
   286→    }
   287→}
   288→
   289→// ============================================================================
   290→// RAZOR OPERATIONS
   291→// ============================================================================
   292→
   293→function razorSequenceAtFrames(frames) {
   294→    if (!isQEAvailable() && !enableQE()) {
   295→        return JSON.stringify({ error: "QE not available for razor operation" });
   296→    }
   297→    try {
   298→        frames = String(frames);
   299→        setTimecodeToFrames();
   300→        qe.project.getActiveSequence().razor(frames);
   301→        return JSON.stringify({ success: true });
   302→    } catch (e) {
   303→        return JSON.stringify({ error: "Razor failed: " + e.message });
   304→    }
   305→}
   306→
   307→function razorSequenceAtSeconds(seconds) {
   308→    try {
   309→        // FIX: Add null check before accessing activeSequence
   310→        if (!app || !app.project || !app.project.activeSequence) {
   311→            $.writeln('[SPLICE] razorSequenceAtSeconds: No active sequence');
   312→            return JSON.stringify({ error: "No active sequence" });
   313→        }
   314→
   315→        var frames = parseInt(Math.round(seconds / app.project.activeSequence.getSettings().videoFrameRate.seconds)).toString();
   316→        return razorSequenceAtFrames(frames);
   317→    } catch (e) {
   318→        $.writeln('[SPLICE] razorSequenceAtSeconds failed: ' + e.message);
   319→        return JSON.stringify({ error: "Razor at seconds failed: " + e.message });
   320→    }
   321→}
   322→
   323→function razorSequenceAtFramesArray(silences_array) {
   324→    if (!isQEAvailable() && !enableQE()) {
   325→        return JSON.stringify({ error: "QE not available for razor operation" });
   326→    }
   327→    try {
   328→        setTimecodeToFrames();
   329→        try {
   330→            silences_array = JSON.parse(silences_array);
   331→        } catch (e) {
   332→            return JSON.stringify({ error: "Invalid JSON input: " + e.message });
   333→        }
   334→        var razorCount = 0;
   335→        for (var i = 0; i < silences_array.length; i++) {
   336→            for (var j = 0; j < silences_array[i].length; j++) {
   337→                var frames = String(silences_array[i][j]);
   338→                qe.project.getActiveSequence().razor(frames);
   339→                razorCount++;
   340→            }
   341→        }
   342→        return JSON.stringify({ success: true, razorCount: razorCount });
   343→    } catch (e) {
   344→        return JSON.stringify({ error: "Razor array failed: " + e.message });
   345→    }
   346→}
   347→
   348→function razorTrackAtFrames(trackType, trackIndex, frames) {
   349→    if (!isQEAvailable() && !enableQE()) {
   350→        return JSON.stringify({ error: "QE not available for track razor" });
   351→    }
   352→    try {
   353→        frames = String(frames);
   354→        setTimecodeToFrames();
   355→        if (trackType == "video") {
   356→            qe.project.getActiveSequence().getVideoTrackAt(trackIndex).razor(frames);
   357→        } else if (trackType == "audio") {
   358→            qe.project.getActiveSequence().getAudioTrackAt(trackIndex).razor(frames);
   359→        }
   360→        return JSON.stringify({ success: true });
   361→    } catch (e) {
   362→        return JSON.stringify({ error: "Track razor failed: " + e.message });
   363→    }
   364→}
   365→
   366→function razorTrackAtSeconds(trackType, trackIndex, seconds) {
   367→    try {
   368→        // FIX: Add null check before accessing activeSequence
   369→        if (!app || !app.project || !app.project.activeSequence) {
   370→            $.writeln('[SPLICE] razorTrackAtSeconds: No active sequence');
