   473→function makeRemainingClipsContiguousInTrack(silences, deltas, trackType, trackIndex) {
   474→    try {
   475→        silences = JSON.parse(silences);
   476→    } catch (e) {
   477→        return JSON.stringify({ error: "Invalid JSON input for silences: " + e.message });
   478→    }
   479→    try {
   480→        deltas = JSON.parse(deltas);
   481→    } catch (e) {
   482→        return JSON.stringify({ error: "Invalid JSON input for deltas: " + e.message });
   483→    }
   484→
   485→    var clips;
   486→    if (trackType == "video") {
   487→        clips = app.project.activeSequence.videoTracks[trackIndex].clips;
   488→    } else if (trackType == "audio") {
   489→        clips = app.project.activeSequence.audioTracks[trackIndex].clips;
   490→    }
   491→
   492→    for (var i = 0; i < clips.length; i++) {
   493→        var clip = clips[i];
   494→        var end = clip.end.seconds;
   495→        var num_silences = -1;
   496→
   497→        for (var j = 0; j < silences.length; j++) {
   498→            if (end > silences[j][1]) {
   499→                num_silences++;
   500→            }
   501→        }
   502→
   503→        if (num_silences > -1 && num_silences < deltas.length) {
   504→            var delta = deltas[num_silences];
   505→            var t_start = clip.start;
   506→            t_start.seconds = t_start.seconds - delta;
   507→            clip.start = t_start;
   508→            var t_end = clip.end;
   509→            t_end.seconds = t_end.seconds - delta;
   510→            clip.end = t_end;
   511→        }
   512→    }
   513→    return JSON.stringify({ success: true });
   514→}
