  1012→        var seq = app.project.activeSequence;
  1013→        if (!seq) return JSON.stringify({ error: "No active sequence" });
  1014→
  1015→        // Clone the sequence
  1016→        var newSequenceName = seq.name + "_SPLICE";
  1017→        seq.clone();
  1018→
  1019→        // Find the cloned sequence
  1020→        var newSeq = null;
  1021→        for (var i = app.project.sequences.numSequences - 1; i >= 0; i--) {
  1022→            var s = app.project.sequences[i];
  1023→            if (s.name.indexOf(seq.name) === 0 && s.sequenceID !== seq.sequenceID) {
  1024→                newSeq = s;
  1025→                break;
  1026→            }
  1027→        }
  1028→
  1029→        if (!newSeq) {
  1030→            return JSON.stringify({ error: "Failed to clone sequence" });
  1031→        }
  1032→
  1033→        newSeq.name = newSequenceName;
  1034→        app.project.activeSequence = newSeq;
  1035→
  1036→        // Clear all clips from the new sequence
  1037→        clearSequence();
  1038→
  1039→        // Insert segments
  1040→        var currentPosition = 0;
  1041→        var stats = {
  1042→            segmentsInserted: 0,
  1043→            totalDuration: 0
  1044→        };
  1045→
  1046→        for (var i = 0; i < cutList.segments.length; i++) {
  1047→            var segment = cutList.segments[i];
  1048→
  1049→            // Find the source project item
  1050→            var sourceItem = null;
  1051→            if (segment.sourcePath) {
  1052→                sourceItem = findItemByPath(app.project.rootItem, segment.sourcePath);
  1053→            }
  1054→            if (!sourceItem && segment.sourceName) {
  1055→                sourceItem = findItemByName(app.project.rootItem, segment.sourceName);
  1056→            }
  1057→
  1058→            if (!sourceItem) continue;
  1059→
  1060→            // Set color label
  1061→            if (segment.colorHint && SEGMENT_COLORS[segment.colorHint]) {
  1062→                sourceItem.setColorLabel(SEGMENT_COLORS[segment.colorHint]);
  1063→            } else if (segment.type && SEGMENT_COLORS[segment.type]) {
  1064→                sourceItem.setColorLabel(SEGMENT_COLORS[segment.type]);
  1065→            }
  1066→
  1067→            // Insert clip at current position
  1068→            var targetTime = new Time();
  1069→            targetTime.seconds = currentPosition;
  1070→
  1071→            newSeq.videoTracks[0].insertClip(sourceItem, targetTime);
  1072→
  1073→            // Set in/out points
  1074→            var clipDuration = segment.outPoint - segment.inPoint;
  1075→            var videoClips = newSeq.videoTracks[0].clips;
  1076→            var insertedClip = videoClips[videoClips.numItems - 1];
  1077→
  1078→            if (insertedClip) {
  1079→                var inTime = new Time();
  1080→                inTime.seconds = segment.inPoint;
  1081→                insertedClip.inPoint = inTime;
  1082→
  1083→                var outTime = new Time();
  1084→                outTime.seconds = segment.outPoint;
  1085→                insertedClip.outPoint = outTime;
  1086→
  1087→                // Set clip name if takeLabel provided
  1088→                if (segment.takeLabel) {
  1089→                    insertedClip.name = segment.takeLabel;
  1090→                }
  1091→            }
  1092→
  1093→            currentPosition += clipDuration;
  1094→            stats.segmentsInserted++;
  1095→            stats.totalDuration += clipDuration;
  1096→        }
  1097→
  1098→        return JSON.stringify({
  1099→            success: true,
  1100→            sequenceName: newSequenceName,
  1101→            stats: stats
  1102→        });
  1103→    } catch (e) {
  1104→        return JSON.stringify({ error: e.message });
  1105→    }
  1106→}
