   623→// COMBINED PREVIEW (Silences + Takes with colored markers)
   624→// ============================================================================
   625→/**
   626→ * Show combined preview with:
   627→ * - Red markers: silences safe to remove
   628→ * - Yellow markers: silences protected by takes (won't be removed)
   629→ * - Blue markers: takes (for reference)
   630→ */
   631→function showCombinedPreview(safeToRemove, protectedSilences, takes) {
   632→  // Update global state
   633→  safeSilences = safeToRemove;
   634→  previewSilences = [...safeToRemove, ...protectedSilences];
   635→  previewTakes = takes;
   636→  selectedSilenceIndices = new Set(safeToRemove.map((_, i) => i));
   637→
   638→  // Hide other views
   639→  hideAllViews();
   640→
   641→  // Update summary counts
   642→  if (ui.silenceCountDisplay) ui.silenceCountDisplay.textContent = safeToRemove.length;
   643→  if (ui.takeCountDisplay) ui.takeCountDisplay.textContent = takes.length;
   644→  if (ui.selectedCount) ui.selectedCount.textContent = safeToRemove.length;
   645→
   646→  // Build combined items list (sorted by time)
   647→  const items = [
   648→    ...safeToRemove.map((s, i) => ({ type: 'silence', index: i, start: s.start, end: s.end, data: s })),
   649→    ...protectedSilences.map(s => ({ type: 'protected', start: s.start, end: s.end, data: s })),
   650→    ...takes.map(t => ({ type: 'take', start: t.startTime || t.start, end: t.endTime || t.end, data: t }))
   651→  ].sort((a, b) => a.start - b.start);
   652→
   653→  // Build preview list using DocumentFragment
   654→  const fragment = document.createDocumentFragment();
   655→
   656→  items.forEach((item) => {
   657→    const duration = item.end - item.start;
   658→    const div = document.createElement('div');
   659→
   660→    if (item.type === 'silence') {
   661→      // Safe to remove - red marker with checkbox
   662→      div.className = 'preview-item silence-marker';
   663→      div.dataset.index = item.index;
   664→      div.innerHTML = `
   665→        <input type="checkbox" class="preview-item-check" checked data-index="${item.index}">
   666→        <div class="preview-item-info">
   667→          <div class="preview-item-time">${formatTime(item.start)} - ${formatTime(item.end)}</div>
   668→          <div class="preview-item-duration">${duration.toFixed(2)}s silence</div>
   669→        </div>
   670→        <button class="preview-item-seek" data-time="${item.start}" title="Seek to this silence">&gt;</button>
   671→      `;
   672→    } else if (item.type === 'protected') {
   673→      // Protected by take - yellow marker, no checkbox
   674→      div.className = 'preview-item protected-silence';
   675→      div.innerHTML = `
   676→        <div style="width: 20px; margin-right: 10px;"></div>
   677→        <div class="preview-item-info">
   678→          <div class="preview-item-time">${formatTime(item.start)} - ${formatTime(item.end)}</div>
   679→          <div class="preview-item-duration">${duration.toFixed(2)}s (overlaps speech)</div>
   680→        </div>
   681→        <button class="preview-item-seek" data-time="${item.start}" title="Seek">&gt;</button>
   682→      `;
   683→    } else if (item.type === 'take') {
   684→      // Take (speech segment) - blue marker, no checkbox
   685→      div.className = 'preview-item take-marker';
   686→      const textPreview = item.data.text?.substring(0, 40) || 'Speech segment';
   687→      div.innerHTML = `
   688→        <div style="width: 20px; margin-right: 10px;"></div>
   689→        <div class="preview-item-info">
   690→          <div class="preview-item-time">${formatTime(item.start)} - ${formatTime(item.end)}</div>
   691→          <div class="preview-item-duration">${textPreview}${item.data.text?.length > 40 ? '...' : ''}</div>
   692→        </div>
   693→        <button class="preview-item-seek" data-time="${item.start}" title="Seek to take">&gt;</button>
   694→      `;
   695→    }
   696→
   697→    fragment.appendChild(div);
   698→  });
   699→
   700→  if (ui.previewList) {
   701→    ui.previewList.textContent = '';
   702→    ui.previewList.appendChild(fragment);
   703→  }
   704→
   705→  // Reset UI state
   706→  if (ui.selectAllSilences) ui.selectAllSilences.checked = true;
   707→  updateApplyButton();
   708→
   709→  // Show combined preview section
   710→  if (ui.combinedPreview) ui.combinedPreview.style.display = 'block';
   711→}
