   965→async function applySelectedSilences() {
   966→  if (selectedSilenceIndices.size === 0) {
   967→    setStatus('No silences selected');
   968→    return;
   969→  }
   970→
   971→  showProgress('Reading marker positions...');
   972→
   973→  // Read current marker positions from timeline
   974→  // This allows users to manually adjust markers before applying
   975→  const markerSilences = await readMarkersAssilences();
   976→
   977→  let silencesToApply;
   978→  let adjustedCount = 0;
   979→
   980→  if (markerSilences.length > 0) {
   981→    // Use marker positions (may have been adjusted by user)
   982→    silencesToApply = markerSilences;
   983→    adjustedCount = markerSilences.length;
   984→    console.log(`[SPLICE] Using ${adjustedCount} silences from adjusted markers`);
   985→  } else {
   986→    // Fallback to original detection data
   987→    silencesToApply = safeSilences.filter((_, i) => selectedSilenceIndices.has(i));
   988→    console.log(`[SPLICE] Using ${silencesToApply.length} silences from original detection`);
   989→  }
   990→
   991→  if (silencesToApply.length === 0) {
   992→    setStatus('No silences to apply');
   993→    return;
   994→  }
   995→
   996→  // Update global silences for the apply function
   997→  setCurrentSilences(silencesToApply);
   998→
   999→  showProgress('Applying changes...');
  1000→
  1001→  // Apply to timeline
  1002→  const result = await removeSilencesFromTimeline();
  1003→
  1004→  // Clear markers after applying
  1005→  await clearSpliceMarkers();
  1006→
  1007→  // Hide preview
  1008→  if (ui.combinedPreview) ui.combinedPreview.style.display = 'none';
  1009→
  1010→  // Show results
  1011→  showSilenceResults({
  1012→    count: silencesToApply.length,
  1013→    timeSaved: silencesToApply.reduce((sum, s) => sum + (s.end - s.start), 0),
  1014→    clipsModified: result
  1015→  });
  1016→
  1017→  // Show advanced section
  1018→  if (ui.advancedSection) ui.advancedSection.classList.remove('hidden');
  1019→
  1020→  const adjustedInfo = adjustedCount > 0 ? ' (from adjusted markers)' : '';
  1021→  setStatus(`Removed ${silencesToApply.length} silence(s) - ${result} clip(s) modified${adjustedInfo}`);
  1022→
  1023→  // Refresh credits (await to prevent race conditions)
  1024→  try {
  1025→    await refreshCredits();
  1026→  } catch (err) {
  1027→    console.warn('[SPLICE] Credits refresh failed:', err);
  1028→  }
  1029→
  1030→  // Clear preview state
  1031→  previewSilences = [];
  1032→  safeSilences = [];
  1033→  protectedSilences = [];
  1034→  previewTakes = [];
  1035→  selectedSilenceIndices.clear();
  1036→}
