  1172→// ============================================================================
  1173→// BUILD SEQUENCE (v3.5 - Direct DOM Reconstruction)
  1174→// ============================================================================
  1175→
  1176→// 1. ROBUST CONSTANTS (Fixes 'undefined' error)
  1177→// We define these integers explicitly because UXP API versions vary on where they store Enums.
  1178→const ProjectItemType = {
  1179→    CLIP: 1,
  1180→    BIN: 2,
  1181→    ROOT: 3,
  1182→    FILE: 4
  1183→};
  1184→
  1185→/**
  1186→ * Find the first media item in the project bin (recursively)
  1187→ * @param {Object} item - ProjectItem (usually rootItem)
  1188→ * @returns {Promise<Object|null>} First media item found or null
  1189→ */
  1190→async function findFirstMediaItem(item) {
  1191→  if (!item) return null;
  1192→
  1193→  // Fix: Use local Constant instead of app.Constants.ProjectItemType
  1194→  if (item.type === ProjectItemType.CLIP) {
  1195→    return item;
  1196→  }
  1197→
  1198→  // Search Children (Bin or Root)
  1199→  // Note: item.children is a collection, check numItems
  1200→  if (item.children && item.children.numItems > 0) {
  1201→    for (let i = 0; i < item.children.numItems; i++) {
  1202→      const child = item.children[i];
  1203→      const found = await findFirstMediaItem(child);
  1204→      if (found) return found;
  1205→    }
  1206→  }
  1207→
  1208→  return null;
  1209→}
