  1211→/**
  1212→ * Build a sequence from detection data locally
  1213→ * Used for testing via console: await buildSequenceV35(silences)
  1214→ */
  1215→async function buildSequenceV35(silences, takes = []) {
  1216→    if (!window.spliceBuilder) {
  1217→        throw new Error('Builder not initialized. Check if builder.js is loaded.');
  1218→    }
  1219→
  1220→    const app = require('premierepro');
  1221→    const project = await app.Project.getActiveProject();
  1222→    if (!project) throw new Error("No active project found.");
  1223→
  1224→    const rootItem = await project.getRootItem();
  1225→    const sourceProjectItem = await findFirstMediaItem(rootItem);
  1226→
  1227→    if (!sourceProjectItem) {
  1228→        throw new Error('No media items found in project bin.');
  1229→    }
  1230→
  1231→    console.log(`[SPLICE] Found source media: ${sourceProjectItem.name}`);
  1232→
  1233→    return await window.spliceBuilder.buildSequenceFromDetection(silences, takes, sourceProjectItem);
  1234→}
