  1236→/**
  1237→ * Request a cut list from the backend and build the sequence.
  1238→ * Full v3.5 workflow.
  1239→ */
  1240→async function buildSequenceWithCutList() {
  1241→  if (!window.spliceBuilder) {
  1242→    throw new Error('Builder not initialized');
  1243→  }
  1244→
  1245→  // Ensure BACKEND_URL is available
  1246→  const apiUrl = (typeof BACKEND_URL !== 'undefined') ? BACKEND_URL : 'http://localhost:3000';
  1247→
  1248→  if (typeof showProgress === 'function') showProgress('Getting source clip info...');
  1249→
  1250→  const app = require('premierepro');
  1251→  const project = await app.Project.getActiveProject();
  1252→  if (!project) throw new Error("No active project.");
  1253→
  1254→  const rootItem = await project.getRootItem();
  1255→  const sourceProjectItem = await findFirstMediaItem(rootItem);
  1256→
  1257→  if (!sourceProjectItem) {
  1258→    throw new Error('No media items found in project. Please import a media file first.');
  1259→  }
  1260→
  1261→  // Use properties strictly
  1262→  const sourceName = sourceProjectItem.name;
  1263→  const sourcePath = sourceProjectItem.treePath;
  1264→
  1265→  const durationObj = await sourceProjectItem.getDuration();
  1266→  const duration = durationObj ? durationObj.seconds : 0;
  1267→
  1268→  // Retrieve Globals
  1269→  const silences = window.splicePreviewState?.getSelectedSilences() || (typeof safeSilences !== 'undefined' ? safeSilences : []);
  1270→  const takes = (typeof previewTakes !== 'undefined' ? previewTakes : []);
  1271→
  1272→  if (silences.length === 0 && (!takes || takes.length === 0)) {
  1273→     throw new Error('No detection data found. Run analysis first.');
  1274→  }
  1275→
  1276→  if (typeof showProgress === 'function') showProgress('Generating cut list...');
  1277→
  1278→  const response = await fetch(`${apiUrl}/cut-list`, {
  1279→    method: 'POST',
  1280→    headers: getAuthHeaders(),
  1281→    body: JSON.stringify({
  1282→      sourceName,
  1283→      sourcePath,
  1284→      duration,
  1285→      silences,
  1286→      takes
  1287→    })
  1288→  });
  1289→
  1290→  if (!response.ok) {
  1291→    const err = await response.json();
  1292→    throw new Error(err.error || 'Failed to generate cut list');
  1293→  }
  1294→
  1295→  const data = await response.json();
  1296→  const cutList = data.cutList;
  1297→
  1298→  if (typeof showProgress === 'function') showProgress('Building sequence...');
  1299→
  1300→  const result = await window.spliceBuilder.buildSequenceFromCutList(cutList, sourceProjectItem);
  1301→
  1302→  if (!result.success) {
  1303→    throw new Error(result.error || 'Failed to build sequence');
  1304→  }
  1305→
  1306→  return {
  1307→    success: true,
  1308→    clipsAdded: result.clipsAdded,
  1309→    metadata: cutList.metadata
  1310→  };
  1311→}
  1312→
  1313→// Expose functions globally for UI and testing
  1314→if (typeof window !== 'undefined') {
  1315→    window.buildSequenceV35 = buildSequenceV35;
  1316→    window.buildSequenceWithCutList = buildSequenceWithCutList;
  1317→    window.findFirstMediaItem = findFirstMediaItem;
  1318→}
