  1323→async function seekToTime(seconds) {
  1324→  // Validate input
  1325→  if (typeof seconds !== 'number' || isNaN(seconds) || !isFinite(seconds)) {
  1326→    setStatus('Invalid seek time');
  1327→    return;
  1328→  }
  1329→
  1330→  if (seconds < 0) {
  1331→    seconds = 0;
  1332→  }
  1333→
  1334→  try {
  1335→    const context = await getActiveSequence();
  1336→    if (!context) {
  1337→      setStatus('No sequence available');
  1338→      return;
  1339→    }
  1340→
  1341→    const { sequence } = context;
  1342→
  1343→    // Use TickTime API to convert seconds to the format needed
  1344→    const position = ppro.TickTime.createWithSeconds(seconds);
  1345→
  1346→    await sequence.setPlayerPosition(position);
  1347→    setStatus(`Seeked to ${formatTime(seconds)}`);
  1348→  } catch (err) {
  1349→    console.error('[SPLICE] Seek error:', err);
  1350→    setStatus('Seek failed: ' + (err.message || 'Unknown error'));
  1351→  }
  1352→}
