  1471→/**
  1472→ * Initialize batch processing handlers
  1473→ */
  1474→function initBatchHandlers() {
  1475→  const addToBatchBtn = document.getElementById('addToBatchBtn');
  1476→  const startBatchBtn = document.getElementById('startBatchBtn');
  1477→  const clearBatchBtn = document.getElementById('clearBatchBtn');
  1478→  const batchSection = document.getElementById('batchSection');
  1479→
  1480→  // Show batch section in settings or after first detection
  1481→  if (batchSection) {
  1482→    batchSection.classList.remove('hidden');
  1483→  }
  1484→
  1485→  // Add files to batch queue
  1486→  if (addToBatchBtn) {
  1487→    addToBatchBtn.addEventListener('click', async () => {
  1488→      try {
  1489→        const uxpFs = require('uxp').storage.localFileSystem;
  1490→        const files = await uxpFs.getFileForOpening({
  1491→          allowMultiple: true,
  1492→          types: ['wav', 'mp3', 'mp4', 'mov', 'aac', 'm4a']
  1493→        });
  1494→
  1495→        if (files && files.length > 0) {
  1496→          for (const file of files) {
  1497→            const path = file.nativePath || file.name;
  1498→            if (!batchQueue.includes(path)) {
  1499→              batchQueue.push(path);
  1500→            }
  1501→          }
  1502→          updateBatchQueueDisplay();
  1503→          setStatus(`Added ${files.length} file(s) to batch queue`);
  1504→        }
  1505→      } catch (err) {
  1506→        setStatus('Error adding files: ' + err.message);
  1507→      }
  1508→    });
  1509→  }
  1510→
  1511→  // Start batch processing
  1512→  if (startBatchBtn) {
  1513→    startBatchBtn.addEventListener('click', async () => {
  1514→      if (batchQueue.length === 0) {
  1515→        setStatus('No files in batch queue');
  1516→        return;
  1517→      }
  1518→
  1519→      startBatchBtn.disabled = true;
  1520→      addToBatchBtn.disabled = true;
  1521→
  1522→      try {
  1523→        await runBatchProcessing();
  1524→      } finally {
  1525→        startBatchBtn.disabled = false;
  1526→        addToBatchBtn.disabled = false;
  1527→      }
  1528→    });
  1529→  }
  1530→
  1531→  // Clear batch queue
  1532→  if (clearBatchBtn) {
  1533→    clearBatchBtn.addEventListener('click', () => {
  1534→      batchQueue = [];
  1535→      currentBatchJobId = null;
  1536→      updateBatchQueueDisplay();
  1537→      setStatus('Batch queue cleared');
  1538→    });
  1539→  }
  1540→}
