  1542→/**
  1543→ * Update batch queue display
  1544→ */
  1545→function updateBatchQueueDisplay() {
  1546→  const batchEmpty = document.getElementById('batchEmpty');
  1547→  const batchList = document.getElementById('batchList');
  1548→  const startBatchBtn = document.getElementById('startBatchBtn');
  1549→
  1550→  if (batchQueue.length === 0) {
  1551→    if (batchEmpty) batchEmpty.style.display = 'block';
  1552→    if (batchList) batchList.innerHTML = '';
  1553→    if (startBatchBtn) startBatchBtn.disabled = true;
  1554→    return;
  1555→  }
  1556→
  1557→  if (batchEmpty) batchEmpty.style.display = 'none';
  1558→  if (startBatchBtn) startBatchBtn.disabled = false;
  1559→
  1560→  if (batchList) {
  1561→    batchList.innerHTML = batchQueue.map((path, i) => {
  1562→      const fileName = path.split('/').pop();
  1563→      return `<div style="display: flex; justify-content: space-between; align-items: center; padding: 4px 6px; background: #2a2a2a; border-radius: 3px; margin-bottom: 2px; font-size: 9px;">
  1564→        <span style="overflow: hidden; text-overflow: ellipsis; white-space: nowrap; max-width: 180px;" title="${path}">${fileName}</span>
  1565→        <button onclick="removeBatchItem(${i})" style="background: transparent; border: none; color: #888; cursor: pointer; font-size: 10px;">x</button>
  1566→      </div>`;
  1567→    }).join('');
  1568→  }
  1569→}
