  1578→
  1579→/**
  1580→ * Run batch processing
  1581→ */
  1582→async function runBatchProcessing() {
  1583→  const batchProgress = document.getElementById('batchProgress');
  1584→  const batchProgressBar = document.getElementById('batchProgressBar');
  1585→  const batchProgressText = document.getElementById('batchProgressText');
  1586→
  1587→  if (batchProgress) batchProgress.style.display = 'block';
  1588→
  1589→  try {
  1590→    // Get settings
  1591→    const sensitivity = parseInt(document.getElementById('sensitivitySlider')?.value || 50);
  1592→    const params = mapSensitivity(sensitivity);
  1593→
  1594→    // Call batch endpoint
  1595→    const response = await fetch(`${BACKEND_URL}/batch/silences`, {
  1596→      method: 'POST',
  1597→      headers: getAuthHeaders(),
  1598→      body: JSON.stringify({
  1599→        files: batchQueue,
  1600→        options: {
  1601→          sensitivity,
  1602→          threshold: params.dbThreshold,
  1603→          minSilenceLength: params.minDuration,
  1604→          paddingStart: params.padding,
  1605→          paddingEnd: params.padding / 2
  1606→        }
  1607→      })
  1608→    });
  1609→
  1610→    if (!response.ok) {
  1611→      const err = await response.json();
  1612→      throw new Error(err.error || 'Batch request failed');
  1613→    }
  1614→
  1615→    const data = await response.json();
  1616→    currentBatchJobId = data.jobId;
  1617→    setStatus(`Batch job started: ${data.jobId}`);
  1618→
  1619→    // Poll for status
  1620→    await pollBatchStatus(currentBatchJobId, batchProgressBar, batchProgressText);
  1621→
  1622→  } catch (err) {
  1623→    setStatus('Batch error: ' + err.message);
  1624→  } finally {
  1625→    if (batchProgress) batchProgress.style.display = 'none';
  1626→  }
  1627→}
  1628→
  1629→/**
  1630→ * Poll batch job status
  1631→ */
  1632→async function pollBatchStatus(jobId, progressBar, progressText) {
  1633→  const pollInterval = 2000; // 2 seconds
  1634→  const maxPolls = 300; // 10 minutes max
  1635→  let polls = 0;
  1636→
  1637→  while (polls < maxPolls) {
  1638→    try {
  1639→      const response = await fetch(`${BACKEND_URL}/batch/status/${jobId}`, {
  1640→        headers: getAuthHeaders()
  1641→      });
  1642→
  1643→      if (!response.ok) {
  1644→        throw new Error('Failed to get batch status');
  1645→      }
  1646→
  1647→      const data = await response.json();
  1648→      const job = data.job;
  1649→
  1650→      // Update progress
  1651→      if (progressBar) progressBar.value = job.progress.percentage;
  1652→      if (progressText) progressText.textContent = `${job.progress.completed + job.progress.failed} / ${job.progress.total} files`;
  1653→
  1654→      // Check if complete
  1655→      if (job.status !== 'processing') {
  1656→        if (job.status === 'completed') {
  1657→          setStatus(`Batch complete: ${job.progress.completed} files processed`);
  1658→        } else if (job.status === 'completed_with_errors') {
  1659→          setStatus(`Batch complete with ${job.progress.failed} error(s)`);
  1660→        } else {
  1661→          setStatus('Batch failed');
  1662→        }
  1663→
  1664→        // Clear queue on completion
  1665→        batchQueue = [];
  1666→        updateBatchQueueDisplay();
  1667→        return;
  1668→      }
  1669→
  1670→      await new Promise(resolve => setTimeout(resolve, pollInterval));
  1671→      polls++;
  1672→    } catch (err) {
  1673→      setStatus('Polling error: ' + err.message);
  1674→      return;
  1675→    }
  1676→  }
  1677→
  1678→  setStatus('Batch timed out');
  1679→}
