  1776→    ui.presetsList.addEventListener('click', (e) => {
  1777→      const editBtn = e.target.closest('.preset-edit-btn');
  1778→      const deleteBtn = e.target.closest('.preset-delete-btn');
  1779→
  1780→      if (editBtn) {
  1781→        const presetId = editBtn.dataset.presetId;
  1782→        editPreset(presetId);
  1783→      }
  1784→
  1785→      if (deleteBtn) {
  1786→        const presetId = deleteBtn.dataset.presetId;
  1787→        const presetName = deleteBtn.dataset.presetName;
  1788→        deletePresetWithConfirm(presetId, presetName);
  1789→      }
  1790→    });
  1791→  }
  1792→}
  1793→
  1794→/**
  1795→ * Refresh the preset dropdown with all presets (built-in + custom)
  1796→ */
  1797→function refreshPresetDropdown() {
  1798→  if (!ui.presetSelector) return;
  1799→
  1800→  const presets = getAllPresets();
  1801→  const currentValue = ui.presetSelector.value;
  1802→
  1803→  // Clear existing options
  1804→  ui.presetSelector.innerHTML = '';
  1805→
  1806→  // Add all presets
  1807→  presets.forEach(preset => {
  1808→    const option = document.createElement('option');
  1809→    option.value = preset.id;
  1810→
  1811→    // Format: "Name - Description" or just "Name"
  1812→    const desc = preset.description ? ` - ${preset.description}` : '';
  1813→    option.textContent = preset.name + desc;
  1814→
  1815→    // Add custom marker for custom presets
  1816→    if (!preset.isBuiltIn) {
  1817→      option.textContent = `${preset.name}${desc}`;
  1818→      option.style.color = '#28a745';
  1819→    }
  1820→
  1821→    ui.presetSelector.appendChild(option);
  1822→  });
  1823→
  1824→  // Restore selection if it still exists
  1825→  if ([...ui.presetSelector.options].some(opt => opt.value === currentValue)) {
  1826→    ui.presetSelector.value = currentValue;
  1827→  } else {
  1828→    ui.presetSelector.value = 'custom';
  1829→  }
  1830→}
  1831→
  1832→/**
  1833→ * Show the preset modal for creating a new preset
  1834→ */
  1835→function showPresetModal(editId = null) {
  1836→  if (!ui.presetModal) return;
  1837→
  1838→  // Reset form
  1839→  ui.presetNameInput.value = '';
  1840→  ui.presetDescInput.value = '';
  1841→  ui.presetEditId.value = '';
  1842→  ui.presetSelectedIcon.value = 'settings';
  1843→  selectPresetIcon('settings');
  1844→  hidePresetError();
  1845→
  1846→  if (editId) {
  1847→    // Edit mode
  1848→    const preset = getPresetById(editId);
  1849→    if (preset && !preset.isBuiltIn) {
  1850→      ui.presetModalTitle.textContent = 'Edit Preset';
  1851→      ui.presetNameInput.value = preset.name;
  1852→      ui.presetDescInput.value = preset.description || '';
  1853→      ui.presetEditId.value = editId;
  1854→      ui.presetSelectedIcon.value = preset.icon || 'settings';
  1855→      selectPresetIcon(preset.icon || 'settings');
  1856→      ui.savePresetConfirmBtn.textContent = 'Update Preset';
  1857→    }
  1858→  } else {
  1859→    // Create mode
  1860→    ui.presetModalTitle.textContent = 'Save Preset';
  1861→    ui.savePresetConfirmBtn.textContent = 'Save Preset';
  1862→  }
  1863→
  1864→  ui.presetModal.classList.remove('hidden');
  1865→  ui.presetNameInput.focus();
  1866→}
  1867→
  1868→/**
  1869→ * Hide the preset modal
  1870→ */
  1871→function hidePresetModal() {
  1872→  if (ui.presetModal) {
  1873→    ui.presetModal.classList.add('hidden');
  1874→  }
  1875→}
  1876→
  1877→/**
  1878→ * Select an icon in the preset modal
  1879→ */
  1880→function selectPresetIcon(iconName) {
  1881→  if (!ui.presetIconPicker || !ui.presetSelectedIcon) return;
  1882→
  1883→  // Update hidden input
  1884→  ui.presetSelectedIcon.value = iconName;
  1885→
  1886→  // Update visual selection
  1887→  ui.presetIconPicker.querySelectorAll('.preset-icon-option').forEach(btn => {
  1888→    if (btn.dataset.icon === iconName) {
  1889→      btn.classList.add('selected');
  1890→    } else {
  1891→      btn.classList.remove('selected');
  1892→    }
  1893→  });
  1894→}
  1895→
  1896→/**
  1897→ * Show error message in preset modal
  1898→ */
  1899→function showPresetError(message) {
  1900→  if (ui.presetModalError) {
  1901→    ui.presetModalError.textContent = message;
  1902→    ui.presetModalError.style.display = 'block';
  1903→  }
  1904→}
  1905→
  1906→/**
  1907→ * Hide error message in preset modal
  1908→ */
  1909→function hidePresetError() {
  1910→  if (ui.presetModalError) {
  1911→    ui.presetModalError.style.display = 'none';
  1912→  }
  1913→}
  1914→
  1915→/**
  1916→ * Save preset from modal (create or update)
  1917→ */
  1918→function savePresetFromModal() {
  1919→  const name = ui.presetNameInput?.value?.trim();
  1920→  const description = ui.presetDescInput?.value?.trim() || '';
  1921→  const icon = ui.presetSelectedIcon?.value || 'settings';
  1922→  const editId = ui.presetEditId?.value;
  1923→
  1924→  if (!name) {
  1925→    showPresetError('Please enter a preset name');
  1926→    return;
  1927→  }
  1928→
  1929→  let result;
  1930→
  1931→  if (editId) {
  1932→    // Update existing preset
  1933→    result = updateCustomPreset(editId, {
  1934→      name,
  1935→      description,
  1936→      icon
  1937→    });
  1938→
  1939→    if (result.success) {
  1940→      setStatus(`Preset "${name}" updated`);
  1941→    }
  1942→  } else {
  1943→    // Create new preset (captures current settings automatically)
  1944→    result = createCustomPreset({
  1945→      name,
  1946→      description,
  1947→      icon
  1948→    });
  1949→
  1950→    if (result.success) {
  1951→      setStatus(`Preset "${name}" saved`);
  1952→      // Select the newly created preset
  1953→      refreshPresetDropdown();
  1954→      if (ui.presetSelector) {
  1955→        ui.presetSelector.value = result.id;
  1956→      }
  1957→    }
  1958→  }
  1959→
  1960→  if (result.success) {
  1961→    hidePresetModal();
  1962→    refreshPresetDropdown();
  1963→  } else {
  1964→    showPresetError(result.error || 'Failed to save preset');
  1965→  }
  1966→}
  1967→
  1968→/**
  1969→ * Show the manage presets modal
  1970→ */
  1971→function showManagePresetsModal() {
  1972→  if (!ui.managePresetsModal) return;
  1973→
  1974→  renderPresetsList();
  1975→  ui.managePresetsModal.classList.remove('hidden');
  1976→}
  1977→
  1978→/**
  1979→ * Hide the manage presets modal
  1980→ */
  1981→function hideManagePresetsModal() {
  1982→  if (ui.managePresetsModal) {
  1983→    ui.managePresetsModal.classList.add('hidden');
  1984→  }
  1985→}
  1986→
  1987→/**
  1988→ * Render the presets list in the manage modal
  1989→ */
  1990→function renderPresetsList() {
  1991→  if (!ui.presetsList) return;
  1992→
  1993→  const presets = getAllPresets();
  1994→  const customPresets = presets.filter(p => !p.isBuiltIn);
  1995→
  1996→  // Show/hide empty state
  1997→  if (ui.noCustomPresets) {
  1998→    ui.noCustomPresets.style.display = customPresets.length === 0 ? 'block' : 'none';
  1999→  }
  2000→
  2001→  // Build HTML
  2002→  const fragment = document.createDocumentFragment();
  2003→
  2004→  presets.forEach(preset => {
  2005→    const div = document.createElement('div');
  2006→    div.className = `preset-list-item ${preset.isBuiltIn ? 'built-in' : 'custom'}`;
  2007→
  2008→    const iconChar = PRESET_ICON_MAP[preset.icon] || '*';
  2009→    const desc = preset.description || (preset.isBuiltIn ? 'Built-in preset' : 'Custom preset');
  2010→
  2011→    if (preset.isBuiltIn) {
  2012→      // Built-in presets: no edit/delete buttons
  2013→      div.innerHTML = `
  2014→        <div class="preset-list-item-icon">${iconChar}</div>
  2015→        <div class="preset-list-item-info">
  2016→          <div class="preset-list-item-name">${preset.name}</div>
  2017→          <div class="preset-list-item-desc">${desc}</div>
  2018→        </div>
  2019→      `;
  2020→    } else {
  2021→      // Custom presets: edit/delete buttons
  2022→      div.innerHTML = `
  2023→        <div class="preset-list-item-icon">${iconChar}</div>
  2024→        <div class="preset-list-item-info">
  2025→          <div class="preset-list-item-name">${preset.name}</div>
  2026→          <div class="preset-list-item-desc">${desc}</div>
  2027→        </div>
  2028→        <div class="preset-list-item-actions">
  2029→          <button class="preset-edit-btn" data-preset-id="${preset.id}" title="Edit preset">E</button>
  2030→          <button class="preset-delete-btn delete" data-preset-id="${preset.id}" data-preset-name="${preset.name}" title="Delete preset">X</button>
  2031→        </div>
  2032→      `;
  2033→    }
  2034→
  2035→    fragment.appendChild(div);
  2036→  });
  2037→
  2038→  ui.presetsList.innerHTML = '';
  2039→  ui.presetsList.appendChild(fragment);
  2040→}
  2041→
  2042→/**
  2043→ * Edit a preset
  2044→ */
  2045→function editPreset(presetId) {
  2046→  hideManagePresetsModal();
  2047→  showPresetModal(presetId);
  2048→}
  2049→
  2050→/**
  2051→ * Delete a preset with confirmation
  2052→ */
  2053→function deletePresetWithConfirm(presetId, presetName) {
  2054→  // Simple confirmation (UXP doesn't have native confirm)
  2055→  const confirmed = confirm(`Delete preset "${presetName}"?`);
  2056→  if (!confirmed) return;
  2057→
  2058→  const result = deleteCustomPreset(presetId);
  2059→
  2060→  if (result.success) {
  2061→    setStatus(`Preset "${presetName}" deleted`);
  2062→    renderPresetsList();
  2063→    refreshPresetDropdown();
  2064→  } else {
  2065→    setStatus('Failed to delete preset: ' + (result.error || 'Unknown error'));
  2066→  }
  2067→}
  2068→
  2069→/**
  2070→ * Apply a preset and update UI controls
  2071→ * Called when user selects a preset from dropdown
  2072→ */
  2073→function applyPresetAndUpdateUI(presetId) {
  2074→  const preset = getPresetById(presetId);
  2075→  if (!preset) return;
  2076→
  2077→  // For custom presets, apply the settings
  2078→  if (!preset.isBuiltIn) {
  2079→    const settings = preset.settings;
  2080→    if (settings) {
  2081→      // Update sensitivity slider
  2082→      if (ui.sensitivitySlider && settings.sensitivity !== undefined) {
  2083→        ui.sensitivitySlider.value = settings.sensitivity;
