  2098→    }
  2099→  } else {
  2100→    // Built-in preset: use applyPreset from settings.js
  2101→    const appliedSettings = applyPreset(presetId);
  2102→
  2103→    // Update sensitivity slider to match preset
  2104→    if (ui.sensitivitySlider && appliedSettings.sensitivity !== undefined) {
  2105→      ui.sensitivitySlider.value = appliedSettings.sensitivity;
  2106→    }
  2107→
  2108→    // Update takes detection checkbox
  2109→    if (ui.enableTakesDetection && appliedSettings.enableTakesDetection !== undefined) {
  2110→      ui.enableTakesDetection.checked = appliedSettings.enableTakesDetection;
  2111→    }
  2112→
  2113→    // Update auto-mark best checkbox
