   103→// Get current preset settings (mock for testing)
   104→function getCurrentPresetSettings() {
   105→  return {
   106→    sensitivity: 50,
   107→    threshold: -32,
   108→    minSilenceLength: 0.5,
   109→    paddingStart: 0.1,
   110→    paddingEnd: 0.1,
   111→    autoMarkBest: true,
   112→    enableTakesDetection: true
   113→  };
   114→}
   115→
   116→// Create custom preset
   117→function createCustomPreset(preset) {
   118→  if (!preset || typeof preset !== 'object') {
   119→    return { success: false, error: 'Invalid preset data' };
   120→  }
   121→
   122→  if (!preset.name || typeof preset.name !== 'string' || !preset.name.trim()) {
   123→    return { success: false, error: 'Preset name is required' };
   124→  }
   125→
   126→  const name = preset.name.trim();
   127→  const data = loadCustomPresets();
   128→  const id = generateUniquePresetId(name, data.presets);
   129→
   130→  const settings = preset.settings && typeof preset.settings === 'object'
   131→    ? { ...preset.settings }
   132→    : getCurrentPresetSettings();
   133→
   134→  if (!settings || typeof settings !== 'object') {
   135→    return { success: false, error: 'Invalid settings' };
   136→  }
   137→
   138→  const newPreset = {
   139→    id,
   140→    name,
   141→    description: preset.description || '',
   142→    icon: preset.icon || 'settings',
   143→    createdAt: new Date().toISOString(),
   144→    settings
   145→  };
   146→
   147→  data.presets[id] = newPreset;
   148→  data.order.push(id);
   149→
   150→  const saved = saveCustomPresets(data);
   151→  if (!saved) {
   152→    return { success: false, error: 'Failed to save preset' };
   153→  }
   154→
   155→  return { success: true, id };
   156→}
   157→
   158→// Update custom preset
   159→function updateCustomPreset(id, updates) {
   160→  if (!id || typeof id !== 'string') {
   161→    return { success: false, error: 'Preset ID is required' };
   162→  }
   163→
   164→  if (!updates || typeof updates !== 'object') {
   165→    return { success: false, error: 'Updates must be an object' };
   166→  }
   167→
   168→  const data = loadCustomPresets();
   169→
   170→  if (!data.presets[id]) {
   171→    return { success: false, error: 'Preset not found' };
   172→  }
   173→
   174→  const existing = data.presets[id];
   175→
   176→  const updated = {
   177→    ...existing,
   178→    updatedAt: new Date().toISOString()
   179→  };
   180→
   181→  if (updates.name !== undefined) {
   182→    if (typeof updates.name !== 'string' || !updates.name.trim()) {
   183→      return { success: false, error: 'Name must be a non-empty string' };
   184→    }
   185→    updated.name = updates.name.trim();
   186→  }
   187→
   188→  if (updates.description !== undefined) {
   189→    updated.description = String(updates.description);
   190→  }
   191→
   192→  if (updates.icon !== undefined) {
   193→    updated.icon = String(updates.icon);
   194→  }
   195→
   196→  if (updates.settings !== undefined) {
   197→    if (typeof updates.settings !== 'object') {
   198→      return { success: false, error: 'Settings must be an object' };
   199→    }
   200→    updated.settings = {
   201→      ...existing.settings,
   202→      ...updates.settings
   203→    };
   204→  }
   205→
   206→  data.presets[id] = updated;
   207→
   208→  const saved = saveCustomPresets(data);
   209→  if (!saved) {
   210→    return { success: false, error: 'Failed to save preset' };
   211→  }
   212→
   213→  return { success: true };
   214→}
   215→
   216→// Get custom preset by ID
   217→function getCustomPreset(id) {
   218→  if (!id || typeof id !== 'string') {
   219→    return null;
   220→  }
   221→  const data = loadCustomPresets();
   222→  return data.presets[id] || null;
   223→}
   224→
   225→// Delete custom preset
   226→function deleteCustomPreset(id) {
   227→  if (!id || typeof id !== 'string') {
   228→    return { success: false, error: 'Preset ID is required' };
   229→  }
   230→
   231→  const data = loadCustomPresets();
   232→
   233→  if (!data.presets[id]) {
   234→    return { success: false, error: 'Preset not found' };
   235→  }
   236→
   237→  delete data.presets[id];
   238→
   239→  const orderIndex = data.order.indexOf(id);
   240→  if (orderIndex !== -1) {
   241→    data.order.splice(orderIndex, 1);
   242→  }
   243→
   244→  const saved = saveCustomPresets(data);
   245→  if (!saved) {
   246→    return { success: false, error: 'Failed to save after deletion' };
   247→  }
   248→
   249→  return { success: true };
   250→}
