   252→// Mock built-in presets (matching settings.js)
   253→const PRESETS = {
   254→  custom: { name: 'Custom', description: 'Your custom settings', icon: 'settings', settings: null },
   255→  podcast: { name: 'Podcast', description: 'Natural pauses', icon: 'mic', settings: { sensitivity: 35 } },
   256→  interview: { name: 'Interview', description: 'Q&A rhythm', icon: 'people', settings: { sensitivity: 50 } },
   257→  reaction: { name: 'Reaction', description: 'Fast-paced', icon: 'bolt', settings: { sensitivity: 70 } },
   258→  tutorial: { name: 'Tutorial', description: 'Teaching pace', icon: 'school', settings: { sensitivity: 30 } },
   259→  vlog: { name: 'Vlog', description: 'Punchy edits', icon: 'videocam', settings: { sensitivity: 65 } }
   260→};
   261→
   262→const BUILT_IN_PRESET_IDS = ['custom', 'podcast', 'interview', 'reaction', 'tutorial', 'vlog'];
   263→
   264→// Is built-in preset
   265→function isBuiltInPreset(id) {
   266→  return BUILT_IN_PRESET_IDS.includes(id);
   267→}
   268→
   269→// Get all presets (built-in + custom)
   270→function getAllPresets() {
   271→  const builtInPresets = BUILT_IN_PRESET_IDS.map(id => {
   272→    const preset = PRESETS[id];
   273→    return {
   274→      id,
   275→      name: preset.name,
   276→      description: preset.description,
   277→      icon: preset.icon,
   278→      settings: preset.settings,
   279→      isBuiltIn: true
   280→    };
   281→  });
   282→
   283→  const customData = loadCustomPresets();
   284→  const customPresets = customData.order.map(id => {
   285→    const preset = customData.presets[id];
   286→    if (!preset) return null;
   287→    return { ...preset, isBuiltIn: false };
   288→  }).filter(Boolean);
   289→
   290→  return [...builtInPresets, ...customPresets];
   291→}
   292→
   293→// Get all preset IDs
   294→function getAllPresetIds() {
   295→  const customData = loadCustomPresets();
   296→  return [...BUILT_IN_PRESET_IDS, ...customData.order];
   297→}
   298→
   299→// Get preset by ID
   300→function getPresetById(id) {
   301→  if (!id || typeof id !== 'string') return null;
   302→
   303→  if (isBuiltInPreset(id)) {
   304→    const preset = PRESETS[id];
   305→    return { id, name: preset.name, description: preset.description, icon: preset.icon, settings: preset.settings, isBuiltIn: true };
   306→  }
   307→
   308→  const custom = getCustomPreset(id);
   309→  if (custom) return { ...custom, isBuiltIn: false };
   310→
   311→  return null;
   312→}
