// =============================================================================
// PHASE 3 TESTS: Duplicate, Settings, Export/Import
// =============================================================================

/**
 * Duplicate a custom preset
 */
function duplicatePreset(id) {
  if (!id || typeof id !== 'string') {
    return { success: false, error: 'Preset ID is required' };
  }

  const original = getPresetById(id);
  if (!original) {
    return { success: false, error: 'Preset not found' };
  }

  let copyName = `${original.name} (Copy)`;
  const data = loadCustomPresets();
  let copyNumber = 1;
  let testId = slugifyPresetName(copyName);
  while (data.presets[testId]) {
    copyNumber++;
    copyName = `${original.name} (Copy ${copyNumber})`;
    testId = slugifyPresetName(copyName);
  }

  return createCustomPreset({
    name: copyName,
    description: original.description || '',
    icon: original.icon || 'settings',
    settings: original.settings ? { ...original.settings } : getCurrentPresetSettings()
  });
}

/**
 * Export all custom presets to JSON string
 */
function exportPresets() {
  try {
    const data = loadCustomPresets();
    const customPresets = data.order.map(id => data.presets[id]).filter(Boolean);

    if (customPresets.length === 0) {
      return { success: false, error: 'No custom presets to export' };
    }

    const exportData = {
      version: 1,
      exportedAt: new Date().toISOString(),
      presets: customPresets
    };

    return {
      success: true,
      data: JSON.stringify(exportData, null, 2),
      count: customPresets.length
    };
  } catch (e) {
    return { success: false, error: 'Failed to export presets: ' + e.message };
  }
}

/**
 * Import presets from JSON string
 */
function importPresets(jsonString, merge = true) {
  try {
    const imported = JSON.parse(jsonString);

    if (!imported || !imported.presets || !Array.isArray(imported.presets)) {
      return { success: false, error: 'Invalid preset file format' };
    }

    let importedCount = 0;
    let skippedCount = 0;

    for (const preset of imported.presets) {
      if (!preset.name || typeof preset.name !== 'string') {
        skippedCount++;
        continue;
      }

      const existingId = slugifyPresetName(preset.name);
      const existing = getCustomPreset(existingId);

      if (existing && !merge) {
        skippedCount++;
        continue;
      }

      if (existing && merge) {
        let newName = `${preset.name} (Imported)`;
        let counter = 1;
        while (getCustomPreset(slugifyPresetName(newName))) {
          counter++;
          newName = `${preset.name} (Imported ${counter})`;
        }
        preset.name = newName;
      }

      const result = createCustomPreset({
        name: preset.name,
        description: preset.description || '',
        icon: preset.icon || 'settings',
        settings: preset.settings || getCurrentPresetSettings()
      });

      if (result.success) {
        importedCount++;
      } else {
        skippedCount++;
      }
    }

    if (importedCount === 0 && skippedCount > 0) {
      return { success: false, error: 'All presets already exist or were invalid', skipped: skippedCount };
    }

    return { success: true, imported: importedCount, skipped: skippedCount };
  } catch (e) {
    if (e instanceof SyntaxError) {
      return { success: false, error: 'Invalid JSON format' };
    }
    return { success: false, error: 'Failed to import presets: ' + e.message };
  }
}

async function testPhase3Duplicate() {
  console.log('\n📦 Phase 3.1: Duplicate Preset');
  console.log('=' .repeat(50));

  // Test 1: Duplicate a custom preset
  await runTest('Duplicate custom preset creates copy', async () => {
    const create = createCustomPreset({
      name: 'Original',
      description: 'Original desc',
      icon: 'mic',
      settings: { sensitivity: 75 }
    });
    assert(create.success, 'Should create original');

    const duplicate = duplicatePreset(create.id);
    assert(duplicate.success, 'Should duplicate successfully');
    assert(duplicate.id !== create.id, 'Should have different ID');

    const copy = getPresetById(duplicate.id);
    assertEqual(copy.name, 'Original (Copy)', 'Copy should have (Copy) suffix');
    assertEqual(copy.description, 'Original desc', 'Description should be copied');
    assertEqual(copy.icon, 'mic', 'Icon should be copied');
    assertEqual(copy.settings.sensitivity, 75, 'Settings should be copied');
  });

  // Test 2: Duplicate with existing copy creates numbered suffix
  await runTest('Duplicate existing copy increments number', async () => {
    createCustomPreset({ name: 'Multi' });
    duplicatePreset('multi');
    const third = duplicatePreset('multi');

    assert(third.success, 'Should duplicate again');
    const copy = getPresetById(third.id);
    assertEqual(copy.name, 'Multi (Copy 2)', 'Should have numbered suffix');
  });

  // Test 3: Duplicate built-in preset
  await runTest('Duplicate built-in preset works', async () => {
    const result = duplicatePreset('podcast');
    assert(result.success, 'Should duplicate built-in');

    const copy = getPresetById(result.id);
    assertEqual(copy.name, 'Podcast (Copy)', 'Should have built-in name with copy suffix');
    assert(!copy.isBuiltIn, 'Copy should not be built-in');
  });

  // Test 4: Duplicate non-existent preset fails
  await runTest('Duplicate non-existent preset fails', async () => {
    const result = duplicatePreset('non-existent');
    assert(!result.success, 'Should fail');
    assertEqual(result.error, 'Preset not found', 'Should have correct error');
  });

  // Test 5: Duplicate with invalid ID fails
  await runTest('Duplicate with invalid ID fails', async () => {
    const result = duplicatePreset(null);
    assert(!result.success, 'Should fail for null');
    assertEqual(result.error, 'Preset ID is required', 'Should have correct error');
  });
}

async function testPhase3Settings() {
  console.log('\n📦 Phase 3.2: Settings in Preset Modal');
  console.log('=' .repeat(50));

  // Test 1: Create preset with custom settings
  await runTest('Create preset with custom sensitivity and threshold', async () => {
    const result = createCustomPreset({
      name: 'Custom Settings Test',
      settings: { sensitivity: 80, threshold: -25 }
    });
    assert(result.success, 'Should create preset');

    const preset = getPresetById(result.id);
    assertEqual(preset.settings.sensitivity, 80, 'Sensitivity should be 80');
    assertEqual(preset.settings.threshold, -25, 'Threshold should be -25');
  });

  // Test 2: Update preset settings
  await runTest('Update preset settings', async () => {
    const create = createCustomPreset({
      name: 'Settings Update Test',
      settings: { sensitivity: 50, threshold: -32 }
    });

    const update = updateCustomPreset(create.id, {
      settings: { sensitivity: 90, threshold: -20 }
    });
    assert(update.success, 'Should update successfully');

    const preset = getPresetById(create.id);
    assertEqual(preset.settings.sensitivity, 90, 'Sensitivity should be updated');
    assertEqual(preset.settings.threshold, -20, 'Threshold should be updated');
  });

  // Test 3: Partial settings update preserves other settings
  await runTest('Partial settings update preserves other fields', async () => {
    const create = createCustomPreset({
      name: 'Partial Update Test',
      settings: { sensitivity: 60, threshold: -30, minSilenceLength: 0.5 }
    });

    updateCustomPreset(create.id, {
      settings: { sensitivity: 70 }
    });

    const preset = getPresetById(create.id);
    assertEqual(preset.settings.sensitivity, 70, 'Sensitivity should be updated');
    assertEqual(preset.settings.threshold, -30, 'Threshold should be preserved');
    assertEqual(preset.settings.minSilenceLength, 0.5, 'Other settings should be preserved');
  });

  // Test 4: Settings are applied when preset is selected (simulated)
  await runTest('Preset settings can be retrieved correctly', async () => {
    const create = createCustomPreset({
      name: 'Apply Test',
      settings: { sensitivity: 85, threshold: -28 }
    });

    const preset = getPresetById(create.id);
    assertEqual(preset.settings.sensitivity, 85, 'Should retrieve sensitivity');
    assertEqual(preset.settings.threshold, -28, 'Should retrieve threshold');
  });
}

async function testPhase3ExportImport() {
  console.log('\n📦 Phase 3.3: Export/Import Presets');
  console.log('=' .repeat(50));

  // Test 1: Export single preset
  await runTest('Export single preset', async () => {
    createCustomPreset({
      name: 'Export Test',
      description: 'For export',
      settings: { sensitivity: 55 }
    });

    const result = exportPresets();
    assert(result.success, 'Should export successfully');
    assertEqual(result.count, 1, 'Should have 1 preset');
    assert(result.data.includes('Export Test'), 'Should contain preset name');
    assert(result.data.includes('"sensitivity": 55'), 'Should contain settings');
  });

  // Test 2: Export multiple presets
  await runTest('Export multiple presets', async () => {
    createCustomPreset({ name: 'Export A' });
    createCustomPreset({ name: 'Export B' });
    createCustomPreset({ name: 'Export C' });

    const result = exportPresets();
    assert(result.success, 'Should export successfully');
    assertEqual(result.count, 3, 'Should have 3 presets');
  });

  // Test 3: Export with no presets fails
  await runTest('Export with no presets fails', async () => {
    const result = exportPresets();
    assert(!result.success, 'Should fail');
    assertEqual(result.error, 'No custom presets to export', 'Should have correct error');
  });

  // Test 4: Import valid JSON
  await runTest('Import valid JSON creates presets', async () => {
    const json = JSON.stringify({
      version: 1,
      presets: [
        { name: 'Imported A', description: 'From file', settings: { sensitivity: 65 } },
        { name: 'Imported B', description: 'From file 2', settings: { sensitivity: 45 } }
      ]
    });

    const result = importPresets(json, true);
    assert(result.success, 'Should import successfully');
    assertEqual(result.imported, 2, 'Should import 2 presets');

    const presetA = getPresetById('imported-a');
    assert(presetA, 'Preset A should exist');
    assertEqual(presetA.settings.sensitivity, 65, 'Settings should be imported');
  });

  // Test 5: Import with merge mode renames duplicates
  await runTest('Import merge mode renames duplicates', async () => {
    createCustomPreset({ name: 'Exists' });

    const json = JSON.stringify({
      version: 1,
      presets: [{ name: 'Exists', settings: { sensitivity: 99 } }]
    });

    const result = importPresets(json, true);
    assert(result.success, 'Should import successfully');
    assertEqual(result.imported, 1, 'Should import 1 preset');

    const imported = getPresetById('exists-imported');
    assert(imported, 'Imported preset should exist with renamed ID');
  });

  // Test 6: Import without merge skips duplicates
  await runTest('Import without merge skips duplicates', async () => {
    createCustomPreset({ name: 'Skip Test' });

    const json = JSON.stringify({
      version: 1,
      presets: [{ name: 'Skip Test' }]
    });

    const result = importPresets(json, false);
    assert(!result.success, 'Should fail when all skipped');
    assertEqual(result.skipped, 1, 'Should skip 1 preset');
  });

  // Test 7: Import invalid JSON fails
  await runTest('Import invalid JSON fails', async () => {
    const result = importPresets('not valid json{{{');
    assert(!result.success, 'Should fail');
    assertEqual(result.error, 'Invalid JSON format', 'Should have correct error');
  });

  // Test 8: Import invalid structure fails
  await runTest('Import invalid structure fails', async () => {
    const json = JSON.stringify({ foo: 'bar' });
    const result = importPresets(json);
    assert(!result.success, 'Should fail');
    assertEqual(result.error, 'Invalid preset file format', 'Should have correct error');
  });

  // Test 9: Round-trip export/import
  await runTest('Round-trip export then import', async () => {
    // Create presets
    createCustomPreset({ name: 'Round Trip A', settings: { sensitivity: 77 } });
    createCustomPreset({ name: 'Round Trip B', settings: { threshold: -22 } });

    // Export
    const exported = exportPresets();
    assert(exported.success, 'Should export');

    // Clear and reimport
    storage.data = {};
    const imported = importPresets(exported.data);
    assert(imported.success, 'Should import');
    assertEqual(imported.imported, 2, 'Should import both');

    // Verify
    const presetA = getPresetById('round-trip-a');
    assertEqual(presetA.settings.sensitivity, 77, 'Settings should survive round-trip');
  });

  // Test 10: Import skips presets with missing name
  await runTest('Import skips presets with missing name', async () => {
    const json = JSON.stringify({
      version: 1,
      presets: [
        { name: 'Valid' },
        { description: 'No name' },
        { name: '' }
      ]
    });

    const result = importPresets(json);
    assert(result.success, 'Should succeed with partial import');
    assertEqual(result.imported, 1, 'Should import 1 valid preset');
    assertEqual(result.skipped, 2, 'Should skip 2 invalid presets');
  });
}

async function testPhase3Integration() {
  console.log('\n📦 Phase 3 Integration Tests');
  console.log('=' .repeat(50));

  // Test 1: Full workflow: create, duplicate, edit settings, export
  await runTest('Full Phase 3 workflow', async () => {
    // Create a preset
    const create = createCustomPreset({
      name: 'Workflow Preset',
      settings: { sensitivity: 60, threshold: -30 }
    });
    assert(create.success, 'Should create preset');

    // Duplicate it
    const duplicate = duplicatePreset(create.id);
    assert(duplicate.success, 'Should duplicate');

    // Edit the copy's settings
    updateCustomPreset(duplicate.id, {
      settings: { sensitivity: 80 }
    });

    // Verify both exist with correct settings
    const original = getPresetById(create.id);
    const copy = getPresetById(duplicate.id);
    assertEqual(original.settings.sensitivity, 60, 'Original unchanged');
    assertEqual(copy.settings.sensitivity, 80, 'Copy updated');

    // Export both
    const exported = exportPresets();
    assert(exported.success, 'Should export');
    assertEqual(exported.count, 2, 'Should have 2 presets');
  });

  // Test 2: Duplicate, modify, delete original
  await runTest('Duplicate then delete original', async () => {
    const create = createCustomPreset({ name: 'To Delete' });
    const duplicate = duplicatePreset(create.id);

    deleteCustomPreset(create.id);

    assert(!getPresetById(create.id), 'Original should be gone');
    assert(getPresetById(duplicate.id), 'Copy should remain');
  });

  // Test 3: Import with settings, then duplicate
  await runTest('Import then duplicate', async () => {
    const json = JSON.stringify({
      version: 1,
      presets: [{ name: 'Imported For Dup', settings: { sensitivity: 88 } }]
    });

    importPresets(json);
    const dup = duplicatePreset('imported-for-dup');
    assert(dup.success, 'Should duplicate imported preset');

    const copy = getPresetById(dup.id);
    assertEqual(copy.settings.sensitivity, 88, 'Settings should be duplicated from import');
  });
}

// =============================================================================
// MAIN TEST RUNNER
// =============================================================================

async function runAllTests() {
  console.log('\n' + '='.repeat(60));
  console.log('🧪 Custom Presets E2E Tests - Phase 1, 2 & 3');
  console.log('='.repeat(60));

  await testFeature1_1();
  await testFeature1_2();
  await testFeature1_3();
  await testFeature1_4();
  await testFeature1_5();
  await testPhase1Integration();
  await testPhase2UI();
  await testPhase3Duplicate();
  await testPhase3Settings();
  await testPhase3ExportImport();
  await testPhase3Integration();

  // Summary
  console.log('\n' + '='.repeat(60));
  console.log('📊 Test Summary');
  console.log('='.repeat(60));
  console.log(`  Total: ${testsPassed + testsFailed}`);
  console.log(`  Passed: ${testsPassed}`);
  console.log(`  Failed: ${testsFailed}`);
  console.log(`  Duration: ${Date.now() - startTime}ms`);

  if (testsFailed > 0) {
    console.log('\n❌ Some tests failed!');
    process.exit(1);
  } else {
    console.log('\n✅ All tests passed!');
    process.exit(0);
  }
}

