    // 2. Delete
    deletePresetWithConfirm('to-delete', 'To Delete');

    // 3. Verify deleted
    const after = getAllPresets().length;
    assertEqual(after, before - 1, 'Should have one less preset');
    assert(!getPresetById('to-delete'), 'Preset should not exist');
