   929→
   930→        // Find the cloned sequence
   931→        var newSeq = null;
   932→        for (var i = app.project.sequences.numSequences - 1; i >= 0; i--) {
   933→            var s = app.project.sequences[i];
   934→            if (s.name.indexOf(seq.name) === 0 && s.sequenceID !== seq.sequenceID) {
   935→                newSeq = s;
