   298→async function analyzeForReframe() {
   299→  const videoPath = getCurrentVideoPath();
   300→  if (!videoPath) {
   301→    showReframeStatus('No video selected', 'error');
   302→    return;
   303→  }
   304→
   305→  socialReframeState.isAnalyzing = true;
   306→  updateAnalyzeButton(true);
