   308→
   309→  try {
   310→    const backendUrl = typeof getBackendUrl === 'function' ? getBackendUrl() : 'https://127.0.0.1:3847';
   311→    const headers = typeof getAuthHeaders === 'function' ? getAuthHeaders() : { 'Content-Type': 'application/json' };
   312→    const fetchFn = typeof fetchWithTimeout === 'function' ? fetchWithTimeout : fetch;
   313→
   314→    const slider = reframeElements.smoothingSlider || document.getElementById('motion-smoothing-slider');
