   374→}
   375→
   376→/**
   377→ * Render analysis results
   378→ */
   379→function renderAnalysisResults(analysis) {
   380→  const container = reframeElements.analysisResults || document.getElementById('reframe-analysis-results');
   381→  if (!container) return;
   382→
   383→  const html = `
   384→    <div class="analysis-summary">
