   391→        <span class="stat-value">${formatDuration(analysis.videoInfo.duration)}</span>
   392→      </div>
   393→      <div class="analysis-stat">
   394→        <span class="stat-label">Resolution</span>
   395→        <span class="stat-value">${analysis.videoInfo.width}x${analysis.videoInfo.height}</span>
   396→      </div>
   397→    </div>
   398→    <div class="analysis-formats">
   399→      ${Object.entries(analysis.suggestions).map(([key, suggestion]) => `
   400→        <div class="format-suggestion" data-format="${key}">
   401→          <span class="format-name">${getAspectRatioName(key)}</span>
   402→          <span class="format-method">${suggestion.method}</span>
   403→          <span class="format-keyframes">${suggestion.totalKeyframes || 0} keyframes</span>
   404→        </div>
   405→      `).join('')}
   406→    </div>
   407→  `;
   408→
   409→  container.innerHTML = html;
