   419→
   420→// ============================================================================
   421→// PREVIEW
   422→// ============================================================================
   423→
   424→/**
   425→ * Update crop preview canvas
   426→ */
   427→function updateCropPreview() {
   428→  const canvas = reframeElements.previewCanvas || document.getElementById('reframe-preview-canvas');
   429→  if (!canvas || !socialReframeState.analysis) return;
   430→
   431→  const ctx = canvas.getContext('2d');
   432→  const { videoInfo, suggestions } = socialReframeState.analysis;
   433→  const aspectId = socialReframeState.selectedAspectRatio;
   434→  const suggestion = suggestions[aspectId];
   435→
   436→  if (!suggestion) return;
   437→
   438→  // Clear canvas
   439→  ctx.clearRect(0, 0, canvas.width, canvas.height);
   440→
   441→  // Draw video frame representation
   442→  const scale = Math.min(canvas.width / videoInfo.width, canvas.height / videoInfo.height);
   443→  const videoW = videoInfo.width * scale;
   444→  const videoH = videoInfo.height * scale;
   445→  const videoX = (canvas.width - videoW) / 2;
   446→  const videoY = (canvas.height - videoH) / 2;
   447→
   448→  // Draw video background
   449→  ctx.fillStyle = '#333';
   450→  ctx.fillRect(videoX, videoY, videoW, videoH);
   451→
   452→  // Draw crop region
   453→  const crop = suggestion.static ? suggestion.crop : suggestion.keyframes[0]?.crop;
   454→  if (crop) {
   455→    ctx.strokeStyle = '#00ff00';
   456→    ctx.lineWidth = 2;
   457→    ctx.strokeRect(
   458→      videoX + crop.x * videoW,
   459→      videoY + crop.y * videoH,
   460→      crop.width * videoW,
   461→      crop.height * videoH
   462→    );
   463→
   464→    // Draw face indicator
   465→    if (socialReframeState.analysis.faceTracking.tracks[0]) {
   466→      const face = socialReframeState.analysis.faceTracking.tracks[0].positions[0];
   467→      if (face) {
   468→        ctx.fillStyle = 'rgba(255, 0, 0, 0.3)';
   469→        ctx.fillRect(
   470→          videoX + face.x * videoW,
   471→          videoY + face.y * videoH,
   472→          face.width * videoW,
   473→          face.height * videoH
   474→        );
   475→      }
   476→    }
   477→  }
   478→
   479→  // Draw safe zones if enabled
   480→  if (socialReframeState.showSafeZones && socialReframeState.safeZones) {
   481→    renderSafeZoneOnCanvas(ctx, videoX, videoY, videoW, videoH);
   482→  }
   483→}
   484→
   485→/**
   486→ * Render safe zone overlay on canvas
   487→ */
   488→function renderSafeZoneOnCanvas(ctx, x, y, width, height) {
   489→  const sz = socialReframeState.safeZones.safeZone;
   490→  if (!sz) return;
   491→
   492→  ctx.fillStyle = 'rgba(255, 0, 0, 0.15)';
   493→
   494→  // Top safe zone
   495→  ctx.fillRect(x, y, width, height * sz.top);
   496→
   497→  // Bottom safe zone
   498→  ctx.fillRect(x, y + height * (1 - sz.bottom), width, height * sz.bottom);
   499→
   500→  // Left safe zone
   501→  ctx.fillRect(x, y, width * sz.left, height);
   502→
   503→  // Right safe zone
   504→  ctx.fillRect(x + width * (1 - sz.right), y, width * sz.right, height);
   505→}
   506→
   507→/**
   508→ * Render safe zone overlay
   509→ */
   510→function renderSafeZoneOverlay() {
   511→  updateCropPreview();
   512→}
   513→
   514→/**
   515→ * Toggle safe zones visibility
   516→ */
   517→function toggleSafeZones() {
   518→  socialReframeState.showSafeZones = !socialReframeState.showSafeZones;
   519→  updateCropPreview();
   520→
   521→  const btn = reframeElements.toggleSafeZonesBtn || document.getElementById('toggle-safe-zones-btn');
   522→  if (btn) {
   523→    btn.classList.toggle('active', socialReframeState.showSafeZones);
   524→  }
   525→}
   526→
   527→// ============================================================================
   528→// EXPORT
   529→// ============================================================================
   530→
   531→/**
   532→ * Export for selected format
   533→ */
   534→async function exportReframe() {
   535→  if (!socialReframeState.analysis) {
   536→    showReframeStatus('Analyze video first', 'error');
   537→    return;
   538→  }
   539→
   540→  socialReframeState.isExporting = true;
   541→  updateExportButton(true);
   542→  showReframeStatus('Generating export data...', 'info');
   543→
   544→  try {
   545→    const backendUrl = typeof getBackendUrl === 'function' ? getBackendUrl() : 'https://127.0.0.1:3847';
   546→    const headers = typeof getAuthHeaders === 'function' ? getAuthHeaders() : { 'Content-Type': 'application/json' };
   547→    const fetchFn = typeof fetchWithTimeout === 'function' ? fetchWithTimeout : fetch;
   548→
   549→    const slider = reframeElements.smoothingSlider || document.getElementById('motion-smoothing-slider');
   550→    const smoothing = slider?.value || 0.3;
   551→
   552→    const response = await fetchFn(`${backendUrl}/reframe/export`, {
   553→      method: 'POST',
   554→      headers,
   555→      body: JSON.stringify({
   556→        videoPath: socialReframeState.currentVideoPath,
   557→        formats: [socialReframeState.selectedAspectRatio],
   558→        settings: {
   559→          quality: 'high',
   560→          motionSmoothing: parseFloat(smoothing)
   561→        }
   562→      })
   563→    });
   564→
   565→    if (!response.ok) {
   566→      const err = await response.json();
   567→      throw new Error(err.error || 'Export failed');
   568→    }
   569→
   570→    const data = await response.json();
   571→    renderExportResults(data);
   572→    showReframeStatus('Export data generated', 'success');
   573→
   574→  } catch (err) {
   575→    console.error('[SPLICE] Reframe export error:', err);
   576→    showReframeStatus(err.message, 'error');
   577→  } finally {
   578→    socialReframeState.isExporting = false;
   579→    updateExportButton(false);
   580→  }
   581→}
   582→
   583→/**
   584→ * Export all formats at once
   585→ */
   586→async function exportAllFormats() {
   587→  if (!socialReframeState.analysis) {
   588→    showReframeStatus('Analyze video first', 'error');
   589→    return;
   590→  }
   591→
   592→  const allFormats = socialReframeState.aspectRatios.map(a => a.id);
   593→
   594→  socialReframeState.isExporting = true;
   595→  updateExportButton(true);
   596→  showReframeStatus('Generating all formats...', 'info');
   597→
   598→  try {
   599→    const backendUrl = typeof getBackendUrl === 'function' ? getBackendUrl() : 'https://127.0.0.1:3847';
   600→    const headers = typeof getAuthHeaders === 'function' ? getAuthHeaders() : { 'Content-Type': 'application/json' };
   601→    const fetchFn = typeof fetchWithTimeout === 'function' ? fetchWithTimeout : fetch;
   602→
   603→    const response = await fetchFn(`${backendUrl}/reframe/export`, {
   604→      method: 'POST',
   605→      headers,
   606→      body: JSON.stringify({
   607→        videoPath: socialReframeState.currentVideoPath,
   608→        formats: allFormats,
   609→        settings: { quality: 'high' }
   610→      })
   611→    });
   612→
   613→    if (!response.ok) {
   614→      const err = await response.json();
   615→      throw new Error(err.error || 'Export failed');
   616→    }
   617→
   618→    const data = await response.json();
   619→    renderExportResults(data);
   620→    showReframeStatus(`Generated ${data.totalFormats} formats`, 'success');
   621→
   622→  } catch (err) {
   623→    console.error('[SPLICE] Reframe batch export error:', err);
   624→    showReframeStatus(err.message, 'error');
   625→  } finally {
   626→    socialReframeState.isExporting = false;
   627→    updateExportButton(false);
   628→  }
   629→}
   630→
   631→/**
   632→ * Render export results
   633→ */
   634→function renderExportResults(data) {
   635→  const container = reframeElements.exportResults || document.getElementById('reframe-export-results');
   636→  if (!container) return;
   637→
   638→  const html = `
   639→    <div class="export-summary">
   640→      <span class="export-count">${data.totalFormats} format(s) ready</span>
   641→    </div>
   642→    <div class="export-list">
   643→      ${data.exports.map(exp => `
   644→        <div class="export-item">
   645→          <span class="export-format">${exp.name}</span>
   646→          <span class="export-dims">${exp.dimensions.width}x${exp.dimensions.height}</span>
   647→          <button class="copy-ffmpeg-btn btn btn-small" data-filter="${encodeURIComponent(exp.ffmpegFilter)}">
   648→            Copy FFmpeg
   649→          </button>
   650→        </div>
   651→      `).join('')}
   652→    </div>
   653→  `;
   654→
   655→  container.innerHTML = html;
   656→
   657→  // Add copy handlers
   658→  container.querySelectorAll('.copy-ffmpeg-btn').forEach(btn => {
   659→    btn.addEventListener('click', () => {
   660→      const filter = decodeURIComponent(btn.dataset.filter);
   661→      navigator.clipboard.writeText(filter).then(() => {
   662→        btn.textContent = 'Copied!';
   663→        setTimeout(() => btn.textContent = 'Copy FFmpeg', 2000);
   664→      });
   665→    });
   666→  });
   667→}
   668→
   669→// ============================================================================
   670→// UI HELPERS
   671→// ============================================================================
   672→
   673→/**
   674→ * Show status message
   675→ */
   676→function showReframeStatus(message, type = 'info') {
   677→  const statusEl = reframeElements.status || document.getElementById('reframe-status');
   678→  if (!statusEl) return;
   679→
   680→  statusEl.textContent = message;
   681→  statusEl.className = `reframe-status reframe-status-${type}`;
   682→
   683→  if (type !== 'error') {
   684→    setTimeout(() => {
   685→      statusEl.textContent = '';
   686→      statusEl.className = 'reframe-status';
   687→    }, 5000);
   688→  }
   689→}
   690→
   691→/**
   692→ * Update analyze button state
   693→ */
   694→function updateAnalyzeButton(isAnalyzing) {
   695→  const btn = reframeElements.analyzeBtn || document.getElementById('analyze-reframe-btn');
   696→  if (!btn) return;
   697→
   698→  btn.disabled = isAnalyzing;
   699→  btn.textContent = isAnalyzing ? 'Analyzing...' : 'Analyze Video';
   700→}
   701→
   702→/**
   703→ * Update export button state
   704→ */
   705→function updateExportButton(isExporting) {
   706→  const btn = reframeElements.exportBtn || document.getElementById('export-reframe-btn');
   707→  if (!btn) return;
   708→
   709→  btn.disabled = isExporting;
   710→  btn.textContent = isExporting ? 'Exporting...' : 'Export';
