   722→
   723→/**
   724→ * Format duration for display
   725→ */
   726→function formatDuration(seconds) {
   727→  const mins = Math.floor(seconds / 60);
   728→  const secs = Math.floor(seconds % 60);
   729→  return `${mins}:${secs.toString().padStart(2, '0')}`;
   730→}
   731→
   732→// ============================================================================
   733→// EXPORTS
   734→// ============================================================================
   735→
   736→// Export for global access
   737→window.spliceSocialReframe = {
   738→  init: initSocialReframe,
   739→  analyze: analyzeForReframe,
