     1→/**
     2→ * SPLICE CEP Text Editor Module
     3→ *
     4→ * Provides UI for text-based video editing.
     5→ * Edit video by editing the transcript text.
     6→ * Delete text to delete video, reorder paragraphs to reorder video.
     7→ */
     8→
     9→// ============================================================================
    10→// STATE
    11→// ============================================================================
    12→
    13→const textEditorState = {
    14→  originalTranscript: null,
    15→  editableTranscript: null,
    16→  currentText: '',
    17→  undoStack: [],
    18→  redoStack: [],
    19→  searchResults: [],
    20→  currentSearchIndex: -1,
    21→  isDirty: false,
    22→  isProcessing: false,
    23→  previewChanges: null,
    24→  lastCutList: null
    25→};
    26→
    27→// Maximum undo history size
    28→const MAX_UNDO_STACK = 50;
    29→
    30→// ============================================================================
    31→// DOM ELEMENT CACHE
    32→// ============================================================================
    33→
    34→const textEditorElements = {
    35→  panel: null,
    36→  content: null,
    37→  status: null,
    38→  wordCount: null,
    39→  dirtyIndicator: null,
    40→  undoBtn: null,
    41→  redoBtn: null,
    42→  applyBtn: null,
    43→  previewBtn: null,
    44→  resetBtn: null,
    45→  buildBtn: null,
    46→  searchInput: null,
    47→  searchBtn: null,
    48→  searchNextBtn: null,
    49→  searchPrevBtn: null,
    50→  searchResults: null,
    51→  replaceInput: null,
    52→  replaceBtn: null,
    53→  replaceAllBtn: null,
    54→  previewContainer: null
    55→};
    56→
    57→/**
    58→ * Cache DOM elements for text editor
    59→ */
    60→function cacheTextEditorElements() {
    61→  textEditorElements.panel = document.getElementById('text-editor-panel');
    62→  textEditorElements.content = document.getElementById('text-editor-content');
    63→  textEditorElements.status = document.getElementById('text-editor-status');
    64→  textEditorElements.wordCount = document.getElementById('text-editor-word-count');
    65→  textEditorElements.dirtyIndicator = document.getElementById('text-editor-dirty');
    66→  textEditorElements.undoBtn = document.getElementById('text-editor-undo-btn');
    67→  textEditorElements.redoBtn = document.getElementById('text-editor-redo-btn');
    68→  textEditorElements.applyBtn = document.getElementById('apply-text-edits-btn');
    69→  textEditorElements.previewBtn = document.getElementById('preview-text-edits-btn');
    70→  textEditorElements.resetBtn = document.getElementById('text-editor-reset-btn');
    71→  textEditorElements.buildBtn = document.getElementById('text-editor-build-btn');
    72→  textEditorElements.searchInput = document.getElementById('text-search-input');
    73→  textEditorElements.searchBtn = document.getElementById('text-search-btn');
    74→  textEditorElements.searchNextBtn = document.getElementById('text-search-next-btn');
    75→  textEditorElements.searchPrevBtn = document.getElementById('text-search-prev-btn');
    76→  textEditorElements.searchResults = document.getElementById('text-search-results');
    77→  textEditorElements.replaceInput = document.getElementById('text-replace-input');
    78→  textEditorElements.replaceBtn = document.getElementById('text-replace-btn');
    79→  textEditorElements.replaceAllBtn = document.getElementById('text-replace-all-btn');
    80→  textEditorElements.previewContainer = document.getElementById('text-editor-preview');
    81→}
    82→
    83→// ============================================================================
    84→// INITIALIZATION
    85→// ============================================================================
    86→
    87→/**
    88→ * Initialize text editor module
    89→ */
    90→function initTextEditor() {
    91→  console.log('[SPLICE] Initializing text editor module');
    92→
    93→  // Cache elements
    94→  cacheTextEditorElements();
    95→
    96→  // Setup event listeners
    97→  setupTextEditorListeners();
    98→
    99→  // Setup keyboard shortcuts
   100→  setupKeyboardShortcuts();
   101→
   102→  console.log('[SPLICE] Text editor module initialized');
   103→}
   104→
   105→/**
   106→ * Setup event listeners for text editor
   107→ */
   108→function setupTextEditorListeners() {
   109→  // Text area input - using event delegation
   110→  document.addEventListener('input', (e) => {
   111→    if (e.target.id === 'text-editor-content') {
   112→      handleTextChange(e.target.value);
   113→    }
   114→  });
   115→
   116→  // Button clicks - using event delegation
   117→  document.addEventListener('click', (e) => {
   118→    const id = e.target.id;
   119→
   120→    switch (id) {
   121→      case 'apply-text-edits-btn':
   122→        applyTextEdits();
   123→        break;
   124→      case 'preview-text-edits-btn':
   125→        previewTextEdits();
   126→        break;
   127→      case 'text-editor-undo-btn':
   128→        undoTextEdit();
   129→        break;
   130→      case 'text-editor-redo-btn':
   131→        redoTextEdit();
   132→        break;
   133→      case 'text-search-btn':
   134→        searchInTranscript();
   135→        break;
   136→      case 'text-search-next-btn':
   137→        navigateSearchResult(1);
   138→        break;
   139→      case 'text-search-prev-btn':
   140→        navigateSearchResult(-1);
   141→        break;
   142→      case 'text-replace-btn':
   143→        replaceInTranscript();
   144→        break;
   145→      case 'text-replace-all-btn':
   146→        replaceAllInTranscript();
   147→        break;
   148→      case 'text-editor-reset-btn':
   149→        resetToOriginal();
   150→        break;
   151→      case 'text-editor-build-btn':
   152→        buildSequenceFromEdits();
   153→        break;
   154→    }
   155→
   156→    // Toggle panel collapse
   157→    if (e.target.closest('.text-editor-header')) {
   158→      const panel = document.getElementById('text-editor-panel');
   159→      if (panel) {
   160→        panel.classList.toggle('collapsed');
   161→      }
   162→    }
   163→  });
   164→
   165→  // Search input enter key
   166→  document.addEventListener('keypress', (e) => {
   167→    if (e.target.id === 'text-search-input' && e.key === 'Enter') {
   168→      e.preventDefault();
   169→      searchInTranscript();
   170→    }
   171→  });
   172→}
   173→
   174→/**
   175→ * Setup keyboard shortcuts
   176→ */
   177→function setupKeyboardShortcuts() {
   178→  document.addEventListener('keydown', (e) => {
   179→    // Only handle if text editor is focused
   180→    const editorContent = textEditorElements.content || document.getElementById('text-editor-content');
   181→    if (!editorContent || document.activeElement !== editorContent) return;
   182→
   183→    // Ctrl/Cmd + Z = Undo
   184→    if ((e.ctrlKey || e.metaKey) && e.key === 'z' && !e.shiftKey) {
   185→      e.preventDefault();
   186→      undoTextEdit();
   187→    }
   188→
   189→    // Ctrl/Cmd + Shift + Z or Ctrl/Cmd + Y = Redo
   190→    if ((e.ctrlKey || e.metaKey) && (e.key === 'Z' || e.key === 'y')) {
   191→      e.preventDefault();
   192→      redoTextEdit();
   193→    }
   194→
   195→    // Ctrl/Cmd + F = Focus search
   196→    if ((e.ctrlKey || e.metaKey) && e.key === 'f') {
   197→      e.preventDefault();
   198→      const searchInput = textEditorElements.searchInput || document.getElementById('text-search-input');
   199→      if (searchInput) searchInput.focus();
   200→    }
   201→  });
   202→}
   203→
   204→// ============================================================================
   205→// TRANSCRIPT LOADING
   206→// ============================================================================
   207→
   208→/**
   209→ * Load transcript into text editor
   210→ * @param {Object} transcript - Transcript from main module
   211→ */
   212→async function loadTranscriptIntoEditor(transcript) {
   213→  if (!transcript) {
   214→    showTextEditorStatus('No transcript available', 'error');
   215→    return;
   216→  }
   217→
   218→  textEditorState.isProcessing = true;
   219→  updateProcessingState(true);
   220→  showTextEditorStatus('Preparing editable transcript...', 'info');
   221→
   222→  try {
   223→    const backendUrl = typeof getBackendUrl === 'function' ? getBackendUrl() : 'https://127.0.0.1:3847';
   224→    const headers = typeof getAuthHeaders === 'function' ? getAuthHeaders() : { 'Content-Type': 'application/json' };
   225→    const fetchFn = typeof fetchWithTimeout === 'function' ? fetchWithTimeout : fetch;
   226→
   227→    const response = await fetchFn(`${backendUrl}/text-edit/prepare`, {
   228→      method: 'POST',
   229→      headers,
   230→      body: JSON.stringify({ transcript })
   231→    });
   232→
   233→    if (!response.ok) {
   234→      const err = await response.json();
   235→      throw new Error(err.error || 'Failed to prepare transcript');
   236→    }
   237→
   238→    const data = await response.json();
   239→
   240→    // Store original and editable transcripts
   241→    textEditorState.originalTranscript = transcript;
   242→    textEditorState.editableTranscript = data.editableTranscript;
   243→    textEditorState.currentText = data.editableTranscript.plainText;
   244→    textEditorState.isDirty = false;
   245→    textEditorState.undoStack = [];
   246→    textEditorState.redoStack = [];
   247→    textEditorState.lastCutList = null;
   248→
   249→    // Render editor
   250→    renderTextEditor();
   251→
   252→    showTextEditorStatus(`Loaded ${data.editableTranscript.totalWords} words`, 'success');
   253→
   254→  } catch (err) {
   255→    console.error('[SPLICE] Text editor load error:', err);
   256→    showTextEditorStatus(err.message, 'error');
   257→  } finally {
   258→    textEditorState.isProcessing = false;
   259→    updateProcessingState(false);
   260→  }
   261→}
   262→
   263→// ============================================================================
   264→// TEXT EDITING
   265→// ============================================================================
   266→
   267→/**
   268→ * Handle text changes in editor
   269→ */
   270→function handleTextChange(newText) {
   271→  // Save current state to undo stack
   272→  if (textEditorState.currentText !== newText) {
   273→    textEditorState.undoStack.push(textEditorState.currentText);
   274→    textEditorState.redoStack = []; // Clear redo stack on new edit
   275→
   276→    // Limit undo stack size
   277→    if (textEditorState.undoStack.length > MAX_UNDO_STACK) {
   278→      textEditorState.undoStack.shift();
   279→    }
   280→  }
   281→
   282→  textEditorState.currentText = newText;
   283→  textEditorState.isDirty = true;
   284→  textEditorState.previewChanges = null; // Clear preview
   285→
   286→  updateEditorStatus();
   287→}
   288→
   289→/**
   290→ * Undo last text edit
   291→ */
   292→function undoTextEdit() {
   293→  if (textEditorState.undoStack.length === 0) {
   294→    showTextEditorStatus('Nothing to undo', 'info');
   295→    return;
   296→  }
   297→
   298→  // Save current to redo stack
   299→  textEditorState.redoStack.push(textEditorState.currentText);
   300→
   301→  // Restore from undo stack
   302→  textEditorState.currentText = textEditorState.undoStack.pop();
   303→
   304→  // Update editor content
   305→  const editor = textEditorElements.content || document.getElementById('text-editor-content');
   306→  if (editor) {
   307→    editor.value = textEditorState.currentText;
   308→  }
   309→
   310→  updateEditorStatus();
   311→  showTextEditorStatus('Undo applied', 'info');
   312→}
   313→
   314→/**
   315→ * Redo last undone edit
   316→ */
   317→function redoTextEdit() {
   318→  if (textEditorState.redoStack.length === 0) {
   319→    showTextEditorStatus('Nothing to redo', 'info');
   320→    return;
   321→  }
   322→
   323→  // Save current to undo stack
   324→  textEditorState.undoStack.push(textEditorState.currentText);
   325→
   326→  // Restore from redo stack
   327→  textEditorState.currentText = textEditorState.redoStack.pop();
   328→
   329→  // Update editor content
   330→  const editor = textEditorElements.content || document.getElementById('text-editor-content');
   331→  if (editor) {
   332→    editor.value = textEditorState.currentText;
   333→  }
   334→
   335→  updateEditorStatus();
   336→  showTextEditorStatus('Redo applied', 'info');
   337→}
   338→
   339→/**
   340→ * Reset to original transcript
   341→ */
   342→function resetToOriginal() {
   343→  if (!textEditorState.editableTranscript) return;
   344→
   345→  // Save current to undo
   346→  if (textEditorState.isDirty) {
   347→    textEditorState.undoStack.push(textEditorState.currentText);
   348→  }
   349→
   350→  textEditorState.currentText = textEditorState.editableTranscript.plainText;
   351→  textEditorState.isDirty = false;
   352→  textEditorState.previewChanges = null;
   353→
   354→  const editor = textEditorElements.content || document.getElementById('text-editor-content');
   355→  if (editor) {
   356→    editor.value = textEditorState.currentText;
   357→  }
   358→
   359→  updateEditorStatus();
   360→  showTextEditorStatus('Reset to original', 'success');
   361→}
   362→
   363→// ============================================================================
   364→// PREVIEW AND APPLY
   365→// ============================================================================
   366→
   367→/**
   368→ * Preview text edits without applying
   369→ */
   370→async function previewTextEdits() {
   371→  if (!textEditorState.editableTranscript || !textEditorState.isDirty) {
   372→    showTextEditorStatus('No changes to preview', 'info');
   373→    return;
   374→  }
   375→
   376→  textEditorState.isProcessing = true;
   377→  updateProcessingState(true);
   378→  showTextEditorStatus('Generating preview...', 'info');
   379→
   380→  try {
   381→    const backendUrl = typeof getBackendUrl === 'function' ? getBackendUrl() : 'https://127.0.0.1:3847';
   382→    const headers = typeof getAuthHeaders === 'function' ? getAuthHeaders() : { 'Content-Type': 'application/json' };
   383→    const fetchFn = typeof fetchWithTimeout === 'function' ? fetchWithTimeout : fetch;
   384→
   385→    const response = await fetchFn(`${backendUrl}/text-edit/preview`, {
   386→      method: 'POST',
   387→      headers,
   388→      body: JSON.stringify({
   389→        transcript: textEditorState.editableTranscript,
   390→        editedText: textEditorState.currentText
   391→      })
   392→    });
   393→
   394→    if (!response.ok) {
   395→      const err = await response.json();
   396→      throw new Error(err.error || 'Preview failed');
   397→    }
   398→
   399→    const data = await response.json();
   400→    textEditorState.previewChanges = data;
   401→
   402→    renderPreview(data);
   403→    showTextEditorStatus(`Preview: ${data.changes.length} changes`, 'success');
   404→
   405→  } catch (err) {
   406→    console.error('[SPLICE] Text edit preview error:', err);
   407→    showTextEditorStatus(err.message, 'error');
   408→  } finally {
   409→    textEditorState.isProcessing = false;
   410→    updateProcessingState(false);
   411→  }
   412→}
   413→
   414→/**
   415→ * Render preview of changes
   416→ */
   417→function renderPreview(preview) {
   418→  const container = textEditorElements.previewContainer || document.getElementById('text-editor-preview');
   419→  if (!container) return;
   420→
   421→  if (!preview.changes || preview.changes.length === 0) {
   422→    container.innerHTML = '<div class="preview-empty">No changes detected</div>';
   423→    return;
   424→  }
   425→
   426→  const html = `
   427→    <div class="preview-summary">
   428→      <span class="preview-stat">${preview.summary.deletions} deletions</span>
   429→      <span class="preview-stat">${preview.summary.reorderings} reorders</span>
   430→      <span class="preview-stat">${preview.impact.percentageRemoved}% removed</span>
   431→    </div>
   432→    <div class="preview-changes">
   433→      ${preview.changes.map(change => `
   434→        <div class="preview-change preview-change-${change.type}">
   435→          <span class="change-type">${change.type}</span>
   436→          <span class="change-desc">${change.description}</span>
   437→          <span class="change-time">${change.timeRange}</span>
   438→        </div>
   439→      `).join('')}
   440→    </div>
   441→  `;
   442→
   443→  container.innerHTML = html;
   444→}
   445→
   446→/**
   447→ * Apply text edits and generate cut list
   448→ */
   449→async function applyTextEdits() {
   450→  if (!textEditorState.editableTranscript || !textEditorState.isDirty) {
   451→    showTextEditorStatus('No changes to apply', 'info');
   452→    return;
   453→  }
   454→
   455→  textEditorState.isProcessing = true;
   456→  updateProcessingState(true);
   457→  showTextEditorStatus('Applying edits...', 'info');
   458→
   459→  try {
   460→    const backendUrl = typeof getBackendUrl === 'function' ? getBackendUrl() : 'https://127.0.0.1:3847';
   461→    const headers = typeof getAuthHeaders === 'function' ? getAuthHeaders() : { 'Content-Type': 'application/json' };
   462→    const fetchFn = typeof fetchWithTimeout === 'function' ? fetchWithTimeout : fetch;
   463→
   464→    // Apply edits
   465→    const applyResponse = await fetchFn(`${backendUrl}/text-edit/apply`, {
   466→      method: 'POST',
   467→      headers,
   468→      body: JSON.stringify({
   469→        transcript: textEditorState.editableTranscript,
   470→        editedText: textEditorState.currentText
   471→      })
   472→    });
   473→
   474→    if (!applyResponse.ok) {
   475→      const err = await applyResponse.json();
   476→      throw new Error(err.error || 'Apply failed');
   477→    }
   478→
   479→    const applyData = await applyResponse.json();
   480→
   481→    // Generate cut list from operations
   482→    const cutListResponse = await fetchFn(`${backendUrl}/text-edit/cut-list`, {
   483→      method: 'POST',
   484→      headers,
   485→      body: JSON.stringify({
   486→        operations: applyData.operations,
   487→        sourceInfo: {
   488→          sourceName: textEditorState.originalTranscript?.source?.name || 'source',
   489→          duration: textEditorState.editableTranscript.duration
   490→        }
   491→      })
   492→    });
   493→
   494→    if (!cutListResponse.ok) {
   495→      const err = await cutListResponse.json();
   496→      throw new Error(err.error || 'Cut list generation failed');
   497→    }
   498→
   499→    const cutListData = await cutListResponse.json();
   500→
   501→    // Store cut list for building
   502→    textEditorState.lastCutList = cutListData.cutList;
   503→
   504→    // Update state
   505→    textEditorState.isDirty = false;
   506→
   507→    showTextEditorStatus(
   508→      `Applied ${applyData.stats.deletions} cuts, ${cutListData.summary.segmentCount} segments`,
   509→      'success'
   510→    );
   511→
   512→    // Show build button
   513→    updateBuildButtonState(true);
   514→
   515→  } catch (err) {
   516→    console.error('[SPLICE] Text edit apply error:', err);
   517→    showTextEditorStatus(err.message, 'error');
   518→  } finally {
   519→    textEditorState.isProcessing = false;
   520→    updateProcessingState(false);
   521→  }
   522→}
   523→
   524→/**
   525→ * Build sequence from applied edits
   526→ */
   527→async function buildSequenceFromEdits() {
   528→  if (!textEditorState.lastCutList) {
   529→    showTextEditorStatus('No edits to build. Apply edits first.', 'error');
   530→    return;
   531→  }
   532→
   533→  showTextEditorStatus('Building sequence...', 'info');
   534→
   535→  try {
   536→    // Use builder to create sequence (CEP version via JSX)
   537→    if (window.spliceBuilder?.buildSequence) {
   538→      await window.spliceBuilder.buildSequence(textEditorState.lastCutList);
   539→      showTextEditorStatus('Sequence built successfully', 'success');
   540→    } else if (typeof buildSequenceFromCutList === 'function') {
   541→      // Fallback to global function if available
   542→      await buildSequenceFromCutList(textEditorState.lastCutList);
   543→      showTextEditorStatus('Sequence built successfully', 'success');
   544→    } else {
   545→      showTextEditorStatus('Builder not available', 'error');
   546→    }
   547→  } catch (err) {
   548→    console.error('[SPLICE] Build sequence error:', err);
   549→    showTextEditorStatus(err.message, 'error');
   550→  }
   551→}
   552→
   553→// ============================================================================
   554→// SEARCH AND REPLACE
   555→// ============================================================================
   556→
   557→/**
   558→ * Search in transcript
   559→ */
   560→async function searchInTranscript() {
   561→  const searchInput = textEditorElements.searchInput || document.getElementById('text-search-input');
   562→  const searchText = searchInput?.value?.trim();
   563→
   564→  if (!searchText) {
   565→    showTextEditorStatus('Enter search text', 'info');
   566→    return;
   567→  }
   568→
   569→  if (!textEditorState.editableTranscript) {
   570→    showTextEditorStatus('No transcript loaded', 'error');
   571→    return;
   572→  }
   573→
   574→  try {
   575→    const backendUrl = typeof getBackendUrl === 'function' ? getBackendUrl() : 'https://127.0.0.1:3847';
   576→    const headers = typeof getAuthHeaders === 'function' ? getAuthHeaders() : { 'Content-Type': 'application/json' };
   577→    const fetchFn = typeof fetchWithTimeout === 'function' ? fetchWithTimeout : fetch;
   578→
   579→    const response = await fetchFn(`${backendUrl}/text-edit/search`, {
   580→      method: 'POST',
   581→      headers,
   582→      body: JSON.stringify({
   583→        transcript: textEditorState.editableTranscript,
   584→        searchText
   585→      })
   586→    });
   587→
   588→    if (!response.ok) {
   589→      const err = await response.json();
   590→      throw new Error(err.error || 'Search failed');
   591→    }
   592→
   593→    const data = await response.json();
   594→    textEditorState.searchResults = data.matches || [];
   595→    textEditorState.currentSearchIndex = data.matches.length > 0 ? 0 : -1;
   596→
   597→    highlightSearchResults();
   598→    showTextEditorStatus(`Found ${data.totalMatches} matches`, 'success');
   599→
   600→  } catch (err) {
   601→    console.error('[SPLICE] Search error:', err);
   602→    showTextEditorStatus(err.message, 'error');
   603→  }
   604→}
   605→
   606→/**
   607→ * Navigate search results
   608→ */
   609→function navigateSearchResult(direction) {
   610→  if (textEditorState.searchResults.length === 0) {
   611→    showTextEditorStatus('No search results', 'info');
   612→    return;
   613→  }
   614→
   615→  textEditorState.currentSearchIndex += direction;
   616→
   617→  // Wrap around
   618→  if (textEditorState.currentSearchIndex >= textEditorState.searchResults.length) {
   619→    textEditorState.currentSearchIndex = 0;
   620→  } else if (textEditorState.currentSearchIndex < 0) {
   621→    textEditorState.currentSearchIndex = textEditorState.searchResults.length - 1;
   622→  }
   623→
   624→  highlightCurrentResult();
   625→  updateSearchResultsDisplay();
   626→}
   627→
   628→/**
   629→ * Highlight search results in editor
   630→ */
   631→function highlightSearchResults() {
   632→  // Update counter
   633→  updateSearchResultsDisplay();
   634→
   635→  if (textEditorState.currentSearchIndex >= 0) {
   636→    highlightCurrentResult();
   637→  }
   638→}
   639→
   640→/**
   641→ * Highlight current search result
   642→ */
   643→function highlightCurrentResult() {
   644→  const result = textEditorState.searchResults[textEditorState.currentSearchIndex];
   645→  if (!result) return;
   646→
   647→  // Scroll to result position in textarea
   648→  const editor = textEditorElements.content || document.getElementById('text-editor-content');
   649→  if (editor) {
   650→    // Find text position and scroll
   651→    const text = editor.value;
   652→    const searchPos = text.toLowerCase().indexOf(result.text.toLowerCase());
   653→    if (searchPos >= 0) {
   654→      editor.setSelectionRange(searchPos, searchPos + result.text.length);
   655→      editor.focus();
   656→    }
   657→  }
   658→
   659→  // Show time info - use formatTime from config.js
   660→  const timeStr = typeof formatTime === 'function' ? formatTime(result.start) : `${Math.floor(result.start)}s`;
   661→  showTextEditorStatus(
   662→    `Match ${textEditorState.currentSearchIndex + 1}/${textEditorState.searchResults.length} at ${timeStr}`,
   663→    'info'
   664→  );
   665→}
   666→
   667→/**
   668→ * Update search results display
   669→ */
   670→function updateSearchResultsDisplay() {
   671→  const display = textEditorElements.searchResults || document.getElementById('text-search-results');
   672→  if (display) {
   673→    const current = textEditorState.currentSearchIndex + 1;
   674→    const total = textEditorState.searchResults.length;
   675→    display.textContent = total > 0 ? `${current}/${total}` : '0/0';
   676→  }
   677→}
   678→
   679→/**
   680→ * Replace in transcript
   681→ */
   682→function replaceInTranscript() {
   683→  const searchInput = textEditorElements.searchInput || document.getElementById('text-search-input');
   684→  const replaceInput = textEditorElements.replaceInput || document.getElementById('text-replace-input');
   685→  const searchText = searchInput?.value?.trim();
   686→  const replaceText = replaceInput?.value || '';
   687→
   688→  if (!searchText) {
   689→    showTextEditorStatus('Enter search text', 'info');
   690→    return;
   691→  }
   692→
   693→  // Replace first occurrence in current text
   694→  const newText = textEditorState.currentText.replace(
   695→    new RegExp(escapeRegex(searchText), 'i'),
   696→    replaceText
   697→  );
   698→
   699→  if (newText !== textEditorState.currentText) {
   700→    handleTextChange(newText);
   701→    const editor = textEditorElements.content || document.getElementById('text-editor-content');
   702→    if (editor) editor.value = newText;
   703→    showTextEditorStatus('Replaced 1 occurrence', 'success');
   704→  } else {
   705→    showTextEditorStatus('No match found', 'info');
   706→  }
   707→}
   708→
   709→/**
   710→ * Replace all occurrences in transcript
   711→ */
   712→function replaceAllInTranscript() {
   713→  const searchInput = textEditorElements.searchInput || document.getElementById('text-search-input');
   714→  const replaceInput = textEditorElements.replaceInput || document.getElementById('text-replace-input');
   715→  const searchText = searchInput?.value?.trim();
   716→  const replaceText = replaceInput?.value || '';
   717→
   718→  if (!searchText) {
   719→    showTextEditorStatus('Enter search text', 'info');
   720→    return;
   721→  }
   722→
   723→  const regex = new RegExp(escapeRegex(searchText), 'gi');
   724→  const matches = textEditorState.currentText.match(regex);
   725→  const count = matches ? matches.length : 0;
   726→
   727→  if (count > 0) {
   728→    const newText = textEditorState.currentText.replace(regex, replaceText);
   729→    handleTextChange(newText);
   730→    const editor = textEditorElements.content || document.getElementById('text-editor-content');
   731→    if (editor) editor.value = newText;
   732→    showTextEditorStatus(`Replaced ${count} occurrences`, 'success');
   733→  } else {
   734→    showTextEditorStatus('No matches found', 'info');
   735→  }
   736→}
   737→
   738→/**
   739→ * Escape special regex characters
   740→ */
   741→function escapeRegex(string) {
   742→  return string.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
   743→}
   744→
   745→// ============================================================================
   746→// UI RENDERING
   747→// ============================================================================
   748→
   749→/**
   750→ * Render text editor content
   751→ */
   752→function renderTextEditor() {
   753→  const container = textEditorElements.content || document.getElementById('text-editor-content');
   754→  if (!container) return;
   755→
   756→  container.value = textEditorState.currentText;
   757→  updateEditorStatus();
   758→}
   759→
   760→/**
   761→ * Update editor status display
   762→ */
   763→function updateEditorStatus() {
   764→  const wordCount = textEditorElements.wordCount || document.getElementById('text-editor-word-count');
   765→  const dirtyIndicator = textEditorElements.dirtyIndicator || document.getElementById('text-editor-dirty');
   766→  const undoBtn = textEditorElements.undoBtn || document.getElementById('text-editor-undo-btn');
   767→  const redoBtn = textEditorElements.redoBtn || document.getElementById('text-editor-redo-btn');
   768→
   769→  if (wordCount) {
   770→    const words = textEditorState.currentText.split(/\s+/).filter(w => w.length > 0);
   771→    wordCount.textContent = `${words.length} words`;
   772→  }
   773→
   774→  if (dirtyIndicator) {
   775→    dirtyIndicator.style.display = textEditorState.isDirty ? 'inline' : 'none';
   776→  }
   777→
   778→  if (undoBtn) {
   779→    undoBtn.disabled = textEditorState.undoStack.length === 0;
   780→  }
   781→
   782→  if (redoBtn) {
   783→    redoBtn.disabled = textEditorState.redoStack.length === 0;
   784→  }
   785→}
   786→
   787→/**
   788→ * Update processing state UI
   789→ */
   790→function updateProcessingState(isProcessing) {
   791→  const panel = textEditorElements.panel || document.getElementById('text-editor-panel');
   792→  if (panel) {
   793→    const buttons = panel.querySelectorAll('button');
   794→    buttons.forEach(btn => {
   795→      if (!btn.classList.contains('always-enabled')) {
   796→        btn.disabled = isProcessing;
   797→      }
   798→    });
   799→  }
   800→
   801→  const editor = textEditorElements.content || document.getElementById('text-editor-content');
   802→  if (editor) {
   803→    editor.disabled = isProcessing;
   804→  }
   805→}
   806→
   807→/**
   808→ * Update build button state
   809→ */
   810→function updateBuildButtonState(enabled) {
   811→  const btn = textEditorElements.buildBtn || document.getElementById('text-editor-build-btn');
   812→  if (btn) {
   813→    btn.disabled = !enabled;
   814→    if (enabled) {
   815→      btn.classList.add('ready');
   816→    } else {
   817→      btn.classList.remove('ready');
   818→    }
   819→  }
   820→}
   821→
   822→/**
   823→ * Show status message
   824→ */
   825→function showTextEditorStatus(message, type = 'info') {
   826→  const statusEl = textEditorElements.status || document.getElementById('text-editor-status');
   827→  if (!statusEl) return;
   828→
   829→  statusEl.textContent = message;
   830→  statusEl.className = `text-editor-status text-editor-status-${type}`;
   831→
   832→  if (type !== 'error') {
   833→    setTimeout(() => {
   834→      statusEl.textContent = '';
   835→      statusEl.className = 'text-editor-status';
   836→    }, 5000);
   837→  }
   838→}
   839→
   840→// ============================================================================
   841→// EXPORTS
   842→// ============================================================================
   843→
   844→// Export for global access
   845→window.spliceTextEditor = {
   846→  init: initTextEditor,
   847→  loadTranscript: loadTranscriptIntoEditor,
   848→  applyEdits: applyTextEdits,
   849→  previewEdits: previewTextEdits,
   850→  undo: undoTextEdit,
   851→  redo: redoTextEdit,
   852→  reset: resetToOriginal,
   853→  search: searchInTranscript,
   854→  buildSequence: buildSequenceFromEdits,
   855→  getState: () => ({ ...textEditorState })
   856→};
   857→
   858→// Also expose individual functions for direct access
   859→window.initTextEditor = initTextEditor;
   860→window.loadTranscriptIntoEditor = loadTranscriptIntoEditor;
   861→

