   600→function handleYoutubeUrlChange() {
   601→  const urlInput = musicElements.youtubeUrl || document.getElementById('musicYoutubeUrl');
   602→  const identifyBtn = musicElements.identifyBtn || document.getElementById('musicIdentifyBtn');
   603→
   604→  if (!urlInput || !identifyBtn) return;
   605→
   606→  const url = urlInput.value.trim();
   607→  const isValidUrl = isValidYouTubeUrl(url);
   608→  identifyBtn.disabled = !isValidUrl;
   609→
   610→  if (musicState.identifiedSong) {
   611→    handleClearReference();
   612→  }
