   614→
   615→function isValidYouTubeUrl(url) {
   616→  if (!url) return false;
   617→  const patterns = [
   618→    /youtube\.com\/watch\?v=[\w-]{11}/,
   619→    /youtu\.be\/[\w-]{11}/,
   620→    /youtube\.com\/embed\/[\w-]{11}/
   621→  ];
   622→  return patterns.some(p => p.test(url));
   623→}
