   716→
   717→    const options = {
   718→      mood: moodSelect?.value || 'neutral',
   719→      instruments: instrumentSelect?.value ? [instrumentSelect.value] : [],
   720→      duration: parseInt(durationSelect?.value) || 60,
   721→      prompt: promptInput?.value || '',
   722→      youtubeUrl: musicState.identifiedSong?.sourceUrl || null,
