   947→async function handleVariationPlay(event) {
   948→  const btn = event.target;
   949→  const index = parseInt(btn.dataset.index);
   950→  const url = btn.dataset.url;
   951→
   952→  if (!url) { setMusicStatus('No preview available', 'warning'); return; }
   953→
   954→  musicState.variationPlayers.forEach((player, i) => {
   955→    if (player && i !== index) {
   956→      player.pause();
   957→      const otherBtn = document.querySelector(`.play-btn[data-index="${i}"]`);
   958→      if (otherBtn) otherBtn.textContent = 'Play';
   959→    }
   960→  });
   961→
   962→  if (!musicState.variationPlayers[index]) {
   963→    musicState.variationPlayers[index] = new Audio();
   964→    musicState.variationPlayers[index].addEventListener('ended', () => { btn.textContent = 'Play'; });
   965→  }
   966→
   967→  const player = musicState.variationPlayers[index];
   968→
   969→  if (player.paused || player.src !== url) {
   970→    player.src = url;
   971→    try {
   972→      await player.play();
   973→      btn.textContent = 'Pause';
   974→    } catch (error) {
   975→      console.error('Play error:', error);
   976→      setMusicStatus('Could not play preview', 'error');
   977→    }
   978→  } else {
   979→    player.pause();
   980→    btn.textContent = 'Play';
   981→  }
   982→}
   983→
